    ans = 0

    for num in nums:
      divisor = 0
      for i in range(2, math.isqrt(num) + 1):
        if num % i == 0:
          if divisor == 0:
            divisor = i
          else:
            divisor = 0
            break
      if divisor > 0 and divisor * divisor < num:
        ans += 1 + num + divisor + num // divisor

    return ans

class Solution:
  def longestPrefix(self, s: str) -> str:
    BASE = 26
    HASH = 8_417_508_174_513
    n = len(s)
    maxLength = 0
    pow = 1
    prefixHash = 0  # the hash of s[0..i]
    suffixHash = 0  # the hash of s[j..n)

    def val(c: str) -> int:
      return ord(c) - ord('a')

    j = n - 1
    for i in range(n - 1):
      prefixHash = (prefixHash * BASE + val(s[i])) % HASH
      suffixHash = (val(s[j]) * pow + suffixHash) % HASH
      pow = pow * BASE % HASH
      if prefixHash == suffixHash:
        maxLength = i + 1
      j -= 1

    return s[:maxLength]

