      curr = 0
      for i in range(start, len(s)):
        curr = curr * 10 + int(s[i])
        if curr > 9999999999:
          return False
        if (prev == -1 or curr == prev - 1) and isValid(s, i + 1, curr, segment + 1):
          return True

      return False

    return isValid(s, 0, -1, 0)

class Solution:
  def getMinSwaps(self, num: str, k: int) -> int:
    original = [int(c) for c in num]
    permutated = original.copy()

    for _ in range(k):
      self._nextPermutation(permutated)

    return self._countSteps(original, permutated)

  def _nextPermutation(self, nums: list[int]):
    n = len(nums)

    # From the back to the front, find the first num < nums[i + 1].
    i = n - 2
    while i >= 0:
      if nums[i] < nums[i + 1]:
        break
      i -= 1

    # From the back to the front, find the first num > nums[i] and swap it with nums[i].
    if i >= 0:
      for j in range(n - 1, i, -1):
        if nums[j] > nums[i]:
          nums[i], nums[j] = nums[j], nums[i]
          break

    def reverse(nums, l, r):
      while l < r:
        nums[l], nums[r] = nums[r], nums[l]
        l += 1
        r -= 1

    # Reverse nums[i + 1..n - 1].
    reverse(nums, i + 1, len(nums) - 1)

  def _countSteps(self, A: list[int], B: list[int]) -> int:
    count = 0

    j = 0
    for i in range(len(A)):
      j = i
      while A[i] != B[j]:
        j += 1
      while i < j:
        B[j], B[j - 1] = B[j - 1], B[j]
        j -= 1
        count += 1

    return count

class Solution:
  def distinctNumbers(self, nums: list[int], k: int) -> list[int]:
