class Solution:
  def bestCoordinate(self, towers: list[list[int]], radius: int) -> list[int]:
    MAX = 50
    n = len(towers)
    ans = [0] * 2
    maxQuality = 0

    def dist(tower: list[int], i: int, j: int) -> float:
      """Returns the distance between the tower and the coordinate."""
      return math.sqrt((tower[0] - i)**2 + (tower[1] - j)**2)

    for i in range(MAX + 1):
      for j in range(MAX + 1):
        qualitySum = 0
        for tower in towers:
          q = tower[2]
          d = dist(tower, i, j)
          if d <= radius:
            qualitySum += int(q / (1 + d))
        if qualitySum > maxQuality:
          maxQuality = qualitySum
          ans = [i, j]

    return ans

class Solution:
  def numberOfSets(self, n: int, k: int) -> int:
    MOD = 1_000_000_007

    @functools.lru_cache(None)
    def dp(i: int, k: int, drawing: bool) -> int:
      if k == 0:  # Find a way to draw k segments.
        return 1
      if i == n:  # Reach the end.
        return 0
      if drawing:
        # 1. Keep drawing at i and move to i + 1.
        # 2. Stop at i so decrease k. We can start from i for the next segment.
        return (dp(i + 1, k, True) + dp(i, k - 1, False)) % MOD
      # 1. Skip i and move to i + 1.
      # 2. Start at i and move to i + 1.
      return (dp(i + 1, k, False) + dp(i + 1, k, True)) % MOD

    return dp(0, k, False)

class Solution:
  def maxLengthBetweenEqualCharacters(self, s: str) -> int:
    ans = -1
    lastSeen = {}

    for i, c in enumerate(s):
      if c not in lastSeen:
        lastSeen[c] = i
      else:
        ans = max(ans, i - lastSeen[c] - 1)

    return ans

from dataclasses import dataclass


@dataclass
class Player:
  age: int
  score: int


class Solution:
  def bestTeamScore(self, scores: list[int], ages: list[int]) -> int:
    n = len(scores)
    players = [Player(age, score) for age, score in zip(ages, scores)]
    # dp[i] := the maximum score of choosing the players[0..i] with the
    # players[i] being selected
    dp = [0] * n

    # Sort by age descending, then by score descending
    players.sort(key=lambda x: (-x.age, -x.score))

    for i in range(n):
      # For each player, choose it first
      dp[i] = players[i].score
      # players[j].age >= players[i].age since we sort in descending order.
      # So, we only have to check that players[j].score >= players[i].score.
      for j in range(i):
        if players[j].score >= players[i].score:
          dp[i] = max(dp[i], dp[j] + players[i].score)

    return max(dp)

class UnionFind:
  def __init__(self, n: int):
    self.id = list(range(n))
    self.rank = [0] * n

  def unionByRank(self, u: int, v: int) -> bool:
    i = self.find(u)
    j = self.find(v)
    if i == j:
      return False
    if self.rank[i] < self.rank[j]:
      self.id[i] = j
    elif self.rank[i] > self.rank[j]:
      self.id[j] = i
    else:
      self.id[i] = j
      self.rank[j] += 1
    return True

  def find(self, u: int) -> int:
    if self.id[u] != u:
      self.id[u] = self.find(self.id[u])
    return self.id[u]


class Solution:
  def areConnected(
      self,
      n: int,
      threshold: int,
      queries: list[list[int]],
  ) -> list[bool]:
    uf = UnionFind(n + 1)

    for z in range(threshold + 1, n + 1):
      for x in range(z * 2, n + 1, z):
        uf.unionByRank(z, x)

    return [uf.find(a) == uf.find(b) for a, b in queries]

class Solution:
  def slowestKey(self, releaseTimes: list[int], keysPressed: str) -> str:
