class Solution:
  def minimumCost(self, s: str) -> int:
    n = len(s)
    ans = 0

    for i in range(1, n):
      if s[i] != s[i - 1]:
        # Invert s[0..i - 1] or s[i..n - 1].
        ans += min(i, n - i)

