class Solution:
  def findSubtreeSizes(self, parent: list[int], s: str) -> list[int]:
    n = len(parent)
    ans = [0] * n
    newParent = parent.copy()
    tree = [[] for _ in range(n)]

    for i in range(1, n):
      closest = self._findClosestAncestor(i, parent, s)
      if closest != -1:
        newParent[i] = closest

    for i in range(1, n):
      tree[newParent[i]].append(i)

    self._dfs(tree, 0, ans)
    return ans

  def _findClosestAncestor(self, u: int, parent: list[int], s: str) -> int:
    """
    Returns the closest ancestor of node `u` that has the same value as `u`.
    """
    curr = parent[u]
    while curr != -1:
      if s[curr] == s[u]:
        return curr
      curr = parent[curr]
    return -1

  def _dfs(self, tree: list[list[int]], u: int, ans: list[int]) -> int:
    sz = 1
    for v in tree[u]:
      sz += self._dfs(tree, v, ans)
    ans[u] = sz
    return sz

class Solution:
  def maxScore(
      self,
      n: int,
      k: int,
      stayScore: list[list[int]],
      travelScore: list[list[int]]
  ) -> int:
    # dp[i][j] := the maximum score after i days being at city j
    dp = [[0] * n for _ in range(k + 1)]

    for i in range(1, k + 1):
      for dest in range(n):
        # 1. Stay at the current city.
        dp[i][dest] = dp[i - 1][dest] + stayScore[i - 1][dest]
        # 2. Travel from any other city.
        for curr in range(n):
          if curr != dest:
            dp[i][dest] = max(dp[i][dest],
                              dp[i - 1][curr] + travelScore[curr][dest])

    return max(dp[k])

class Solution:
  def lengthAfterTransformations(self, s: str, t: int) -> int:
    MOD = 1_000_000_007

    def matrixMult(A: list[list[int]], B: list[list[int]]) -> list[list[int]]:
      """Returns A * B."""
      sz = len(A)
      C = [[0] * sz for _ in range(sz)]
      for i in range(sz):
        for j in range(sz):
          for k in range(sz):
            C[i][j] += A[i][k] * B[k][j]
            C[i][j] %= MOD
      return C

    def matrixPow(M: list[list[int]], n: int) -> list[list[int]]:
      """Returns M^n."""
      if n == 0:
        return [[1 if i == j else 0  # identity matrix
                for j in range(len(M))]
                for i in range(len(M))]
      if n % 2 == 1:
        return matrixMult(M, matrixPow(M, n - 1))
      return matrixPow(matrixMult(M, M), n // 2)

    # T[i][j] := the number of ways to transform ('a' + i) to ('a' + j)
    T = self._getTransformationMatrix()
    poweredT = matrixPow(T, t)
    count = [0] * 26
    lengths = [0] * 26

    for c in s:
      count[ord(c) - ord('a')] += 1

    for i in range(26):
      for j in range(26):
        lengths[j] += count[i] * poweredT[i][j]
        lengths[j] %= MOD

    return sum(lengths) % MOD

  def _getTransformationMatrix(self) -> list[list[int]]:
    T = [[0] * 26 for _ in range(26)]
    # 'z' -> 'ab'
    T[25][0] = 1
    T[25][1] = 1
    # 'a' -> 'b', 'b' -> 'c', ..., 'y' -> 'z'
    for i in range(25):
      T[i][i + 1] = 1
    return T

class Solution:
  def subsequencePairCount(self, nums: list[int]) -> int:
    MOD = 1_000_000_007

    @functools.lru_cache(None)
    def dp(i: int, x: int, y: int) -> int:
      if i == len(nums):
        return int(x > 0 and x == y)
      # 1. Skip nums[i]
      skip = dp(i + 1, x, y)
      # 2. Pick nums[i] in the first subsequence
      take1 = dp(i + 1, math.gcd(x, nums[i]), y)
      # 3. Pick nums[i] in the second subsequence
      take2 = dp(i + 1, x, math.gcd(y, nums[i]))
      return (skip + take1 + take2) % MOD

    return dp(0, 0, 0)

