class Solution:
  def minRunesToAdd(
      self,
      n: int,
      crystals: list[int],
      flowFrom: list[int],
      flowTo: list[int]
  ) -> int:
    graph = [[] for _ in range(n)]
    reversedGraph = [[] for _ in range(n)]

    for u, v in zip(flowFrom, flowTo):
      graph[u].append(v)
      reversedGraph[v].append(u)

    # Identify Strongly Connected Components (SCC) using Kosaraju's Algorithm.
    seen = set()
    orderStack = []
    componentIds = [-1] * n
    componentCount = 0

    for i in range(n):
      if i not in seen:
        self._kosaraju(graph, i, seen, orderStack)

    while orderStack:
      u = orderStack.pop()
      if componentIds[u] == -1:
        self._identifySCC(reversedGraph, u, componentIds, componentCount)
        componentCount += 1

    # Track crystal-containing components and inter-component edges.
    hasCrystal = [False] * componentCount
    hasInterComponentEdge = [False] * componentCount

    for u in crystals:
      hasCrystal[componentIds[u]] = True

    for u, v in zip(flowFrom, flowTo):
      id1 = componentIds[u]
      id2 = componentIds[v]
      if id1 != id2:  # Edge is inter-component.
        hasInterComponentEdge[id2] = True

    return sum(not hasCrystal[i] and not hasInterComponentEdge[i]
               for i in range(componentCount))

  def _kosaraju(
      self,
      graph: list[list[int]],
      u: int,
      seen: set[int],
      orderStack: list
  ) -> None:
    """Creates a topological order stack using Kosaraju's Algorithm."""
    seen.add(u)
    for v in graph[u]:
      if v not in seen:
        self._kosaraju(graph, v, seen, orderStack)
    orderStack.append(u)

  def _identifySCC(
      self,
      reversedGraph: list[list[int]],
      u: int,
      componentIds: list[int],
      componentId: int
  ) -> None:
    """Assigns component IDs during SCC identification in the second DFS."""
    if componentIds[u] != -1:
      return
    componentIds[u] = componentId
    for v in reversedGraph[u]:
      if componentIds[v] == -1:
        self._identifySCC(reversedGraph, v, componentIds, componentId)

class Solution:
  def buttonWithLongestTime(self, events: list[list[int]]) -> int:
    ans = 0
    maxTimeTaken = 0
    prevTime = 0

    for index, time in events:
      timeTaken = time - prevTime
      if timeTaken > maxTimeTaken or timeTaken == maxTimeTaken and index < ans:
        maxTimeTaken = timeTaken
        ans = index
      prevTime = time

    return ans

class Solution:
  def beautifulSplits(self, nums: list[int]) -> int:
    n = len(nums)
    # z[start][i] := the z array of nums[i..n) with respect to nums[start..n)
    z = [self._zFunction(nums, start)
         for start in range(n)]
    # nums1 | nums2 | nums3 = nums[0..i] | nums[i + 1..j] | nums[j + 1..n - 1]
    return sum((j - i >= i + 1 and z[0][i + 1] >= i + 1)  # nums1 is a prefix of nums2
               or z[i + 1][j + 1] >= j - i  # nums2 is a suffix of nums3.
               for i in range(n - 2)
               for j in range(i + 1, n - 1))

  def _zFunction(self, nums: list[int], start: int) -> list[int]:
    """
    Returns the z array, where z[i] is the length of the longest prefix of
    nums[i..n) which is also a prefix of nums[start..n).
    """
    n = len(nums)
    z = [0] * n
    l = start
    r = start
    for i in range(1 + start, n):
      if i < r:
        z[i] = min(r - i, z[i - l + start])
      while i + z[i] < n and nums[z[i] + start] == nums[i + z[i]]:
        z[i] += 1
      if i + z[i] > r:
        l = i
        r = i + z[i]
    return z

class Solution:
  def makeStringGood(self, s: str) -> int:
    count = [0] * 26
    for c in s:
      count[ord(c) - ord('a')] += 1
    return min(self._getMinOperations(count, target)
               for target in range(1, max(count) + 1))

  def _getMinOperations(self, count: list[int], target: int) -> int:
    # dp[i] represents the minimum number of operations to make the frequency of
    # (i..25)-th (0-indexed) letters equal to `target`.
    dp = [0] * 27

    for i in range(25, -1, -1):
      # 1. Delete all the i-th letters.
      deleteAllToZero = count[i]
      # 2. Insert/delete the i-th letters to have `target` number of letters.
      deleteOrInsertToTarget = abs(target - count[i])
      dp[i] = min(deleteAllToZero, deleteOrInsertToTarget) + dp[i + 1]
      if i + 1 < 26 and count[i + 1] < target:
        nextDeficit = target - count[i + 1]
        # Make the frequency of the i-th letter equal to the `target` or 0.
        needToChange = count[i] if count[i] <= target else count[i] - target
        changeToTarget = (
            # 3. Change all the i-th letters to the next letter and then
            # insert the remaining deficit for the next letter.
            needToChange + (nextDeficit - needToChange) if nextDeficit > needToChange
            # 4. Change `nextDeficit` i-th letters to the next letter and
            # then delete the remaining i-th letters.
            else nextDeficit + (needToChange - nextDeficit)
        )
        dp[i] = min(dp[i], changeToTarget + dp[i + 2])

    return dp[0]

