class Solution:
  def longestSubsequence(self, nums: list[int]) -> int:
    mx = max(nums)
    # dp[num][diff] := the length of the longest subsequence ending in `num`
    # s.t. the last absolute difference between consecutive elements is `diff`
    dp = [[0] * (mx + 1) for _ in range(mx + 1)]

    for num in nums:
      for prev in range(1, mx + 1):
        diff = abs(num - prev)
        dp[num][diff] = max(dp[num][diff], dp[prev][diff] + 1)
      # dp[num][diff] := max(dp[num][j]) for j >= diff
      for j in range(mx - 1, -1, -1):
        dp[num][j] = max(dp[num][j], dp[num][j + 1])

    return max(map(max, dp))

class Solution:
  def maxLength(self, nums: list[int]) -> int:
    n = len(nums)
    ans = 0

    for i in range(n):
      prod = 1
      l = 1
      g = 0
      for j in range(i, n):
        prod *= nums[j]
        l = math.lcm(l, nums[j])
        g = math.gcd(g, nums[j])
        if prod == l * g:
          ans = max(ans, j - i + 1)

    return ans

class Solution:
  def calculateScore(self, s: str) -> int:
    ans = 0
    indices = [[] for _ in range(26)]

    for i, c in enumerate(s):
      index = ord(c) - ord('a')
      oppositeIndex = 25 - index
      if indices[oppositeIndex]:
        ans += i - indices[oppositeIndex].pop()
      else:
        indices[index].append(i)

    return ans

from dataclasses import dataclass


@dataclass(frozen=True)
class T:
  weight: int
  selected: tuple[int]

  def __iter__(self):
    yield self.weight
    yield self.selected


class Solution:
  def maximumWeight(self, intervals: list[list[int]]) -> list[int]:
    intervals = sorted((*interval, i) for i, interval in enumerate(intervals))

    @functools.lru_cache(None)
    def dp(i: int, quota: int) -> T:
      """
      Returns the maximum weight and the selected intervals for intervals[i..n),
      where `quota` is the number of intervals that can be selected.
      """
      if i == len(intervals) or quota == 0:
        return T(0, ())

      skip = dp(i + 1, quota)

      _, r, weight, originalIndex = intervals[i]
      j = bisect.bisect_right(intervals, (r, math.inf))
      nextRes = dp(j, quota - 1)
      pick = T(weight + nextRes.weight,
               sorted((originalIndex, *nextRes.selected)))
      return (pick if (pick.weight > skip.weight or
                       pick.weight == skip.weight and pick.selected < skip.selected)
              else skip)

    return list(dp(0, 4).selected)

class Solution:
  def zigzagTraversal(self, grid: list[list[int]]) -> list[int]:
    zigzag = [row[::-1] if i % 2 else row for i, row in enumerate(grid)]
    return [num for row in zigzag for num in row][::2]

class Solution:
  def maximumAmount(self, coins: list[list[int]]) -> int:
    m = len(coins)
    n = len(coins[0])
    # dp[i][j][k] := the maximum profit at position (i, j) with k remaining
    # neutralizations
    dp = [[[-math.inf] * 4 for _ in range(n)] for _ in range(m)]

    # Base case: the robot starts at the top-left corner.
    dp[0][0][2] = coins[0][0]
    if coins[0][0] < 0:
      dp[0][0][1] = 0  # Neutralize the robber.

    for i in range(m):
      for j in range(n):
        for k in range(3):  # for each number of remaining neutralizations
          if i > 0:
            dp[i][j][k] = max(dp[i][j][k],
                              dp[i - 1][j][k] + coins[i][j],
                              dp[i - 1][j][k + 1])
          if j > 0:
            dp[i][j][k] = max(dp[i][j][k],
                              dp[i][j - 1][k] + coins[i][j],
                              dp[i][j - 1][k + 1])

    return max(dp[-1][-1])

class Solution:
  def minMaxWeight(self, n: int, edges: list[list[int]], threshold: int) -> int:
    MAX = 1000000
    reversedGraph = [[] for _ in range(n)]

    for u, v, w in edges:
      reversedGraph[v].append((u, w))

    l = 1
    r = MAX + 1

    while l < r:
      m = (l + r) // 2
      if self._dfs(reversedGraph, 0, m, set()) == n:
        r = m
      else:
        l = m + 1

    return -1 if l == MAX + 1 else l

  def _dfs(
      self,
      reversedGraph: list[list[tuple]],
      u: int,
      maxWeight: int,
      seen: set[int]
  ) -> int:
    """Returns the number of nodes reachable from u with weight <= maxWeight."""
    res = 1
    seen.add(u)
    for v, w in reversedGraph[u]:
      if w > maxWeight or v in seen:
        continue
      res += self._dfs(reversedGraph, v, maxWeight, seen)
    return res

class Solution:
  def maxAdjacentDistance(self, nums: list[int]) -> int:
    return max(abs(nums[i] - nums[i - 1])
               for i in range(len(nums)))

class Solution:
  def minCost(self, arr: list[int], brr: list[int], k: int) -> int:
    def cost(arr: list[int], brr: list[int]) -> int:
      return sum(abs(a - b) for a, b in zip(arr, brr))
    return min(cost(arr, brr), cost(sorted(arr), sorted(brr)) + k)

class Solution:
  def subarraySum(self, nums: list[int]) -> int:
    prefix = list(itertools.accumulate(nums, initial=0))
    return sum(prefix[i + 1] - prefix[max(0, i - num)]
               for i, num in enumerate((nums)))

class Solution:
  def minCost(self, n: int, costs: list[list[int]]) -> int:
    INVALID_COLOR = 3

    def getValidColors(prevColor: int) -> list[int]:
      return [color for color in range(3) if color != prevColor]

    @functools.lru_cache(None)
    def minCost(i: int, prevLeftColor: int, prevRightColor: int) -> int:
      if i == len(costs) // 2:
        return 0
      res = math.inf
      for leftColor in getValidColors(prevLeftColor):
        for rightColor in getValidColors(prevRightColor):
          if leftColor == rightColor:
            continue
          leftCost = costs[i][leftColor]
          rightCost = costs[-1 - i][rightColor]
          res = min(res, leftCost + rightCost +
                    minCost(i + 1, leftColor, rightColor))
      return res

    return minCost(0, INVALID_COLOR, INVALID_COLOR)

class Solution:
  def minUnlockedIndices(self, nums: list[int], locked: list[int]) -> int:
    first2 = next((i for i, x in enumerate(nums) if x == 2), -1)
    first3 = next((i for i, x in enumerate(nums) if x == 3), -1)
    last1 = next((i for i, x in reversed(list(enumerate(nums))) if x == 1), -1)
    last2 = next((i for i, x in reversed(list(enumerate(nums))) if x == 2), -1)
    if first3 != -1 and last1 != -1 and first3 < last1:
      return -1
    return (sum(locked[i] == 1 for i in range(first2, last1)
                if first2 != -1 and last1 != -1) +
            sum(locked[i] == 1 for i in range(first3, last2)
                if first3 != -1 and last2 != -1))

from dataclasses import dataclass


@dataclass(frozen=True)
class OfflineUser:
  returnTimestamp: int
  userId: int

  def __lt__(self, other):
    return self.returnTimestamp < other.returnTimestamp


class Solution:
  def countMentions(
      self,
      numberOfUsers: int,
      events: list[list[str]]
  ) -> list[int]:
    ans = [0] * numberOfUsers
    online = [True] * numberOfUsers
    offlineQueue = []  # min-heap to track users that are offline
    allMentionsCount = 0

    events.sort(key=lambda x: (int(x[1]), -ord(x[0][0])))

    for eventType, t, messageContent in events:
      timestamp = int(t)
      # Bring users back online if their offline period has ended.
      while offlineQueue and offlineQueue[0].returnTimestamp <= timestamp:
        user = heapq.heappop(offlineQueue)
        online[user.userId] = True
      if eventType == "MESSAGE":
        match messageContent:
          case "ALL":
            allMentionsCount += 1
          case "HERE":
            for userId in range(numberOfUsers):
              if online[userId]:
                ans[userId] += 1
          case _:
            for userId in [int(part[2:]) for part in messageContent.split()]:
              ans[userId] += 1
      elif eventType == "OFFLINE":
        userId = int(messageContent)
        online[userId] = False
        # Add to queue to bring back online after 60 units.
        heapq.heappush(offlineQueue, OfflineUser(timestamp + 60, userId))

    # Add the "ALL" mentions to all users.
    for userId in range(numberOfUsers):
      ans[userId] += allMentionsCount
    return ans

class Solution:
  def maxFrequency(self, nums: list[int], k: int) -> int:
    return nums.count(k) + max(self._kadane(nums, target, k)
                               for target in range(1, 51)
                               if target != k)

  def _kadane(self, nums: list[int], target: int, k: int) -> int:
    """
    Returns the maximum achievable frequency of `k` by Kakane's algorithm,
    where each `target` in subarrays is transformed to `k`.
    """
    maxSum = 0
    sum = 0
    for num in nums:
      if num == target:
        sum += 1
      elif num == k:
        sum -= 1
      if sum < 0:  # Reset sum if it becomes negative (Kadane's spirit).
        sum = 0
      maxSum = max(maxSum, sum)
    return maxSum

from enum import Enum


class State(Enum):
  INIT = 0
  VISITING = 1
  VISITED = 2


class Solution:
  def supersequences(self, words: list[str]) -> list[list[int]]:
    ans = []
    edges = [(string.ascii_lowercase.index(words[0]),
              string.ascii_lowercase.index(words[1]))
             for words in words]
    nodes = sorted({u for u, _ in edges} | {v for _, v in edges})
    letterToIndex = {letter: i for i, letter in enumerate(nodes)}
    graph = [[] for _ in range(len(nodes))]

    for u, v in edges:
      graph[letterToIndex[u]].append(letterToIndex[v])

    for doubledSubset in self._getMinimumSubsets(graph):
      freq = [0] * 26
      for letter in nodes:
        freq[letter] = 1
      for index in doubledSubset:
        freq[nodes[index]] = 2
      ans.append(freq)

    return ans

  def _getMinimumSubsets(self, graph: list[list[int]]) -> list[tuple[int]]:
    """
    Returns a list of the minimum subsets of nodes that do not create a cycle
    when skipped.
    """
    n = len(graph)
    for subsetSize in range(n + 1):
      doubleSubsets = []
      for doubledSubset in itertools.combinations(range(n), subsetSize):
        if not self._hasCycleSkipping(graph, set(doubledSubset)):
          doubleSubsets.append(doubledSubset)
      if doubleSubsets:
        return doubleSubsets
    return []

  def _hasCycleSkipping(
      self,
      graph: list[list[int]],
      doubledSubset: set[int]
  ) -> bool:
    """
    Returns True if there is a cycle in the `graph` when skipping any edges
    whose both endpoints are in `doubledSubset`.
    """
    states = [State.INIT] * len(graph)

    def hasCycle(u: int) -> bool:
      if states[u] == State.VISITING:
        return True
      if states[u] == State.VISITED:
        return False
      states[u] = State.VISITING
      if u not in doubledSubset:
        for v in graph[u]:
          if v in doubledSubset:
            continue
          if hasCycle(v):
            return True
      states[u] = State.VISITED
      return False

    return any(hasCycle(i) for i in range(len(graph)))

class Solution:
  def permute(self, n: int) -> list[list[int]]:
    ans = []
    used = [False] * (n + 1)

    def dfs(path: list[int]) -> None:
      if len(path) == n:
        ans.append(path.copy())
        return
      for num in range(1, n + 1):
        if used[num]:
          continue
        if path and path[-1] % 2 == num % 2:
          continue
        used[num] = True
        path.append(num)
        dfs(path)
        path.pop()
        used[num] = False

    dfs([])
    return ans

class Solution:
  def findValidPair(self, s: str) -> str:
    count = collections.Counter(s)
    return next((a + b
                for a, b in itertools.pairwise(s)
                if a != b and count[a] == int(a) and count[b] == int(b)), '')

class Solution:
  def maxDifference(self, s: str) -> int:
    count = collections.Counter(s)
    maxOdd = max((freq for freq in count.values()
                  if freq % 2 == 1), default=0)
    minEven = min((freq for freq in count.values()
                   if freq % 2 == 0), default=len(s))
    return maxOdd - minEven

class Solution:
  def maxDistance(self, s: str, k: int) -> int:
    return max(self._flip(s, k, 'NE'), self._flip(s, k, 'NW'),
               self._flip(s, k, 'SE'), self._flip(s, k, 'SW'))

  def _flip(self, s: str, k: int, direction: str) -> int:
    res = 0
    pos = 0
    opposite = 0

    for c in s:
      if c in direction:
        pos += 1
      else:
        pos -= 1
        opposite += 1
      res = max(res, pos + 2 * min(k, opposite))

    return res

class Solution:
  def sortMatrix(self, grid: list[list[int]]) -> list[list[int]]:
    n = len(grid)
    ans = [[0] * n for _ in range(n)]
    diag = collections.defaultdict(list)

    for i, row in enumerate(grid):
      for j, num in enumerate(row):
        diag[i - j].append(num)

    for key in diag:
      diag[key].sort(reverse=key < 0)

    for i in range(n):
      for j in range(n):
        ans[i][j] = diag[i - j].pop()

    return ans

class Solution:
  def assignElements(self, groups: list[int], elements: list[int]) -> list[int]:
    ans = []
    elementToMinIndex = {}

    for i, element in enumerate(elements):
      if element not in elementToMinIndex:
        elementToMinIndex[element] = i

    for num in groups:
      ans.append(self._getMinIndex(num, elementToMinIndex))

    return ans

  def _getMinIndex(self, num: int, elementToMinIndex: dict[int, int]) -> int:
    res = math.inf
    i = 1
    while i * i <= num:
      if num % i != 0:
        continue
      if i in elementToMinIndex:
        res = min(res, elementToMinIndex[i])
      if num // i != i and (num // i) in elementToMinIndex:
        res = min(res, elementToMinIndex[num // i])
      i += 1
    return -1 if res == math.inf else res

class Solution:
  def countSubstrings(self, s: str) -> int:
    ans = 0
    # dp[i][num][rem] := the number of first `i` digits of s that have a
    # remainder of `rem` when divided by `num`
    dp = [[[0] * 10 for _ in range(10)] for _ in range(len(s) + 1)]

    for i in range(1, len(s) + 1):
      digit = int(s[i - 1])
      for num in range(1, 10):
        for rem in range(num):
          dp[i][num][(rem * 10 + digit) % num] += dp[i - 1][num][rem]
        dp[i][num][digit % num] += 1
      ans += dp[i][digit][0]

    return ans

class Solution:
  def maxScore(self, points: list[int], m: int) -> int:
    def isPossible(minVal: int, m: int) -> bool:
      """
      Returns True if it is possible to achieve the maximum minimum value `x`
      with `m` number of moves.
      """
      moves = 0
      prevMoves = 0  # to track remaining moves from the previous point
      for i, point in enumerate(points):
        required = (minVal + point - 1) // point  # ceil(minVal / point)
        required = max(0, required - prevMoves)
        if required > 0:
          moves += 2 * required - 1
          prevMoves = required - 1
        elif i + 1 < len(points):
          moves += 1
          prevMoves = 0
        if moves > m:
          return False
      return True

    l = 0
    r = (m + 1) // 2 * points[0] + 1

    while l < r:
      mid = (l + r + 1) // 2
      if isPossible(mid, m):
        l = mid
      else:
        r = mid - 1

    return l

class Solution:
  def maxStudentsOnBench(self, students: list[list[int]]) -> int:
    benchToStudents = collections.defaultdict(set)
    for studentId, benchId in students:
      benchToStudents[benchId].add(studentId)
    return max(map(len, benchToStudents.values()), default=0)

class Solution:
  def sumOfGoodNumbers(self, nums: list[int], k: int) -> int:
    return sum(num for i, num in enumerate(nums)
               if (i - k < 0 or num > nums[i - k])
               and (i + k >= len(nums) or num > nums[i + k]))

class Solution:
  def hasSpecialSubstring(self, s: str, k: int) -> bool:
    return any(len(list(group)) == k for _, group in groupby(s))

class Solution:
  def lenOfVDiagonal(self, grid: list[list[int]]) -> int:
    DIRS = ((-1, 1), (1, 1), (1, -1), (-1, -1))

    @functools.lru_cache(None)
    def dfs(i: int, j: int, turned: bool, num: int, dir: int) -> int:
      if i < 0 or i == len(grid) or j < 0 or j == len(grid[0]):
        return 0
      if grid[i][j] != num:
        return 0

      nextNum = 0 if num == 2 else 2
      dx, dy = DIRS[dir]
      res = 1 + dfs(i + dx, j + dy, turned, nextNum, dir)

      if not turned:
        nextDir = (dir + 1) % 4
        nextDx, nextDy = DIRS[nextDir]
        res = max(res, 1 + dfs(i + nextDx, j + nextDy, 1, nextNum, nextDir))

      return res

    return max((1 + dfs(i + dx, j + dy, 0, 2, d)
                for i, row in enumerate(grid)
                for j, num in enumerate(row)
                if num == 1
                for d, (dx, dy) in enumerate(DIRS)),
               default=0)

class Solution:
  def longestCommonPrefix(self, s: str, t: str) -> int:
    i = 0  # s's index
    j = 0  # t's index
    canSkip = True

    while i < len(s) and j < len(t):
      if s[i] == t[j]:
        i += 1
        j += 1
      elif canSkip:
        i += 1
        canSkip = False
      else:
        return j

    return j

class Solution:
  def maxSum(self, grid: list[list[int]], limits: list[int], k: int) -> int:
    minHeap = []

    for row, limit in zip(grid, limits):
      row.sort(reverse=True)
      for i in range(limit):
        heapq.heappush(minHeap, row[i])
        if len(minHeap) == k + 1:
          heapq.heappop(minHeap)

    return sum(minHeap)

class Solution:
  def maxCoins(self, lane1: list[int], lane2: list[int]) -> int:
    n = len(lane1)
    # dp[i][k] := the maximum number of coins at i-th mile with k switches
    dp = [[-math.inf] * 3 for _ in range(n)]
    dp[0][0] = lane1[0]
    dp[0][1] = lane2[0]

    for i in range(1, n):
      dp[i][0] = max(0, dp[i - 1][0]) + lane1[i]
      dp[i][1] = max(0, dp[i - 1][0], dp[i - 1][1]) + lane2[i]
      dp[i][2] = max(dp[i - 1][1], dp[i - 1][2]) + lane1[i]

    return max(map(max, dp))

class Solution:
  def transformArray(self, nums: list[int]) -> list[int]:
    return ([0] * sum(num % 2 == 0 for num in nums) +
            [1] * sum(num % 2 == 1 for num in nums))

class Solution:
  def countArrays(self, original: list[int], bounds: list[list[int]]) -> int:
    mn, mx = bounds[0]

    for i in range(1, len(original)):
      diff = original[i] - original[i - 1]
      mn = max(mn + diff, bounds[i][0])
      mx = min(mx + diff, bounds[i][1])

    return max(0, mx - mn + 1)

class Solution:
  def minCost(self, nums: list[int]) -> int:
    n = len(nums)

    @functools.lru_cache(None)
    def dp(last: int, i: int) -> int:
      if i == n:  # Single element left.
        return nums[last]
      if i == n - 1:  # Two elements left.
        return max(nums[last], nums[i])
      a = max(nums[i], nums[i + 1]) + dp(last, i + 2)
      b = max(nums[last], nums[i]) + dp(i + 1, i + 2)
      c = max(nums[last], nums[i + 1]) + dp(i, i + 2)
      return min(a, b, c)

    return dp(0, 1)

class Solution:
  def permute(self, n: int, k: int) -> list[int]:
    ans = []
    isLookingForEven = True
    remainingNumbers = list(range(1, n + 1))

    for turn in range(n):
      remainingPermutations = (math.factorial((n - 1 - turn) // 2) *
                               math.factorial((n - turn) // 2))
      found = False
      for index, number in enumerate(remainingNumbers):
        if number % 2 != isLookingForEven and (turn > 0 or n % 2 == 1):
          continue
        if k <= remainingPermutations:
          ans.append(remainingNumbers.pop(index))
          isLookingForEven = ans[-1] % 2 == 0
          found = True
          break
        k -= remainingPermutations
      if not found:
        return []

    return ans

class Solution:
  def largestInteger(self, nums: list[int], k: int) -> int:
    if k == len(nums):
      return max(nums)
    count = collections.Counter(nums)
    if k == 1:
      return max([num for num in nums if count[num] == 1], default=-1)
    return max(
        nums[0] if count[nums[0]] == 1 else -1,
        nums[-1] if count[nums[-1]] == 1 else -1
    )

class Solution:
  # Similar to 516. Longest Palindromic Subsequence
  def longestPalindromicSubsequence(self, s: str, k: int) -> int:
    n = len(s)
    # dp[i][j][op] := the length of LPS(s[i..j]) with at most `op` operations
    dp = [[[0] * (k + 1) for _ in range(n)] for _ in range(n)]

    for i in range(n):
      for op in range(k + 1):
        dp[i][i][op] = 1

    for d in range(1, n):
      for i in range(n - d):
        j = i + d
        for op in range(k + 1):
          if s[i] == s[j]:
            dp[i][j][op] = 2 + dp[i + 1][j - 1][op]
          else:
            dp[i][j][op] = max(dp[i + 1][j][op], dp[i][j - 1][op])
            cost = self._getCost(s[i], s[j])
            if cost <= op:
              dp[i][j][op] = max(dp[i][j][op], 2 + dp[i + 1][j - 1][op - cost])

    return dp[0][n - 1][k]

  def _getCost(self, a: str, b: str) -> int:
    dist = abs(ord(a) - ord(b))
    return min(dist, 26 - dist)

class Solution:
  def generateString(self, str1: str, str2: str) -> str:
    n = len(str1)
    m = len(str2)
    sz = n + m - 1
    ans = [None] * sz
    modifiable = [True] * sz

    # 1. Handle all 'T' positions first.
    for i, tf in enumerate(str1):
      if tf == 'T':
        for j, c in enumerate(str2):
          pos = i + j
          if ans[pos] and ans[pos] != c:
            return ''
          ans[pos] = c
          modifiable[pos] = False

    # 2. Fill all remaining positions with 'a'.
    for i in range(sz):
      if not ans[i]:
        ans[i] = 'a'

    # 3. Handle all 'F' positions.
    for i in range(n):
      if str1[i] == 'F' and self._match(ans, i, str2):
        modifiablePos = self._lastModifiablePosition(i, m, modifiable)
        if modifiablePos == -1:
          return ''
        ans[modifiablePos] = 'b'
        modifiable[modifiablePos] = False

    return ''.join(ans)

  def _match(self, ans: list, i: int, s: str) -> bool:
    """Returns True if the substring of ans starting at `i` matches `s`."""
    for j, c in enumerate(s):
      if ans[i + j] != c:
        return False
    return True

  def _lastModifiablePosition(self, i: int, m: int, modifiable: list) -> int:
    """
    Finds the last modifiable position in the substring of ans starting at `i`.
    """
    modifiablePos = -1
    for j in range(m):
      pos = i + j
      if modifiable[pos]:
        modifiablePos = pos
    return modifiablePos

class Solution:
  def maxProfit(self, workers: list[int], tasks: list[list[int]]) -> int:
    totalProfit = 0
    skillToProfits = collections.defaultdict(list)

    for skill, profit in tasks:
      skillToProfits[skill].append(profit)

    for skill in skillToProfits:
      skillToProfits[skill].sort(reverse=True)

    for workerSkill in workers:
      if workerSkill in skillToProfits and skillToProfits[workerSkill]:
        profit = skillToProfits[workerSkill][0]
        skillToProfits[workerSkill].pop(0)
        totalProfit += profit

    return totalProfit + max(max(profits, default=0)
                             for profits in skillToProfits.values())

class Solution:
  def findMaxSum(self, nums1: list[int], nums2: list[int], k: int) -> list[int]:
    ans = [0] * len(nums1)
    numAndIndexes = sorted([(num, i) for i, num in enumerate(nums1)])
    minHeap = []

    firstIndex = numAndIndexes[0][1]
    heapq.heappush(minHeap, nums2[firstIndex])
    summ = nums2[firstIndex]

    for (prevNum, prevIndex), (currNum, currIndex) in itertools.pairwise(numAndIndexes):
      if currNum == prevNum:
        ans[currIndex] = ans[prevIndex]
      else:
        ans[currIndex] = summ
      heapq.heappush(minHeap, nums2[currIndex])
      summ += nums2[currIndex]
      if len(minHeap) == k + 1:
        summ -= heapq.heappop(minHeap)

    return ans

class Solution:
  def applySubstitutions(self, replacements: list[list[str]], text: str) -> str:
    replaceMap = {key: value for key, value in replacements}

    def evaluate(text: str) -> str:
      """
      Evaluates the text and replaces the placeholders with the values
      from the replace_map recursively.
      """
      res = []
      i = 0
      while i < len(text):
        if text[i] == '%':
          j = i + 1 + text[i + 1:].find('%')
          key = text[i + 1:j]
          value = replaceMap[key]
          res.append(evaluate(value))
          i = j + 1
        else:
          res.append(text[i])
          i += 1
      return ''.join(res)

    return evaluate(text)

class Solution:
  def totalNumbers(self, digits: list[int]) -> int:
    nums = set()

    for a, b, c in itertools.permutations(digits, 3):
      if a != 0 and c % 2 == 0:
        nums.add(a * 100 + b * 10 + c)

    return len(nums)

class TrieNode:
  def __init__(self):
    self.children: dict[str, TrieNode] = {}
    self.count = 0


class Trie:
  def __init__(self, k: int):
    self.k = k
    self.root = TrieNode()
    self.prefixLengthsCount = collections.Counter()
    self.prefixLengths = SortedList()

  def insert(self, word: str) -> None:
    node = self.root
    for i, c in enumerate(word):
      sz = i + 1
      node = node.children.setdefault(c, TrieNode())
      node.count += 1
      if node.count >= self.k:
        self.prefixLengthsCount[sz] += 1
        if self.prefixLengthsCount[sz] == 1:
          self.prefixLengths.add(-sz)

  def erase(self, word: str) -> None:
    node = self.root
    for i, c in enumerate(word):
      sz = i + 1
      node = node.children[c]
      if node.count == self.k:
        self.prefixLengthsCount[sz] -= 1
        if self.prefixLengthsCount[sz] == 0:
          self.prefixLengths.remove(-sz)
      node.count -= 1

  def getLongestCommonPrefix(self) -> int:
    return 0 if not self.prefixLengths else -self.prefixLengths[0]


class Solution:
  def longestCommonPrefix(self, words: list[str], k: int) -> list[int]:
    ans = []
    trie = Trie(k)

    for word in words:
      trie.insert(word)

    for word in words:
      trie.erase(word)
      ans.append(trie.getLongestCommonPrefix())
      trie.insert(word)

    return ans

class Solution:
  def maxSum(self, nums: list[int]) -> int:
    mx = max(nums)
    if mx <= 0:
      return mx
    return sum(max(0, num) for num in set(nums))

class Solution:
  def minZeroArray(self, nums: list[int], queries: list[list[int]]) -> int:
    if all(num == 0 for num in nums):
      return 0

    n = len(nums)
    subsetSums = [{0} for _ in range(n)]

    for k, (l, r, val) in enumerate(queries):
      for i in range(l, r + 1):
        newSums = {subsetSum + val for subsetSum in subsetSums[i]}
        subsetSums[i].update(newSums)
      if all(nums[i] in subsetSums[i] for i in range(n)):
        return k + 1

    return -1

class Solution:
  def beautifulNumbers(self, l: int, r: int) -> int:
    @functools.lru_cache(None)
    def dp(
        s: str,
        i: int,
        tight: bool,
        isLeadingZero: bool,
        hasZero: bool,
        sum: int,
        prod: int,
    ) -> int:
      if i == len(s):
        if isLeadingZero:
          return 0
        return 1 if hasZero or prod % sum == 0 else 0
      if not isLeadingZero and hasZero and not tight:
        return 10 ** (len(s) - i)

      res = 0
      maxDigit = int(s[i]) if tight else 9

      for d in range(maxDigit + 1):
        nextTight = tight and (d == maxDigit)
        nextIsLeadingZero = isLeadingZero and d == 0
        nextHasZero = not nextIsLeadingZero and d == 0
        nextProd = 1 if nextIsLeadingZero else prod * d
        res += dp(s, i + 1, nextTight, nextIsLeadingZero,
                  nextHasZero, sum + d, nextProd)

      return res

    return (dp(str(r), 0, tight=True, isLeadingZero=True, hasZero=False, sum=0, prod=1) -
            dp(str(l - 1), 0, tight=True, isLeadingZero=True, hasZero=False, sum=0, prod=1))

class TrieNode:
  def __init__(self):
    self.children: dict[str, TrieNode] = {}
    self.isWord = False


class Trie:
  def __init__(self):
    self.root = TrieNode()

  def insert(self, number: str) -> None:
    node: TrieNode = self.root
    for d in number:
      node = node.children.setdefault(d, TrieNode())
    node.isWord = True

  def search(self, number: str) -> bool:
    """
    Returns True if the number is a prefix of any number in the trie or any
    number in the trie is a prefix of the number.
    """
    node: TrieNode = self.root
    for d in number:
      if d not in node.children:
        return False
      node = node.children[d]
      if node.isWord:
        return True
    return True  # The number is a prefix of some number in the trie.


class Solution:
  def phonePrefix(self, numbers: list[str]) -> bool:
    trie = Trie()

    for number in numbers:
      if trie.search(number):
        return False
      trie.insert(number)

    return True

class Solution:
  def maxContainers(self, n: int, w: int, maxWeight: int) -> int:
    return min(n * n, maxWeight // w)

class UnionFind:
  def __init__(self, n: int):
    self.count = n
    self.id = list(range(n))
    self.rank = [0] * n

  def unionByRank(self, u: int, v: int) -> None:
    i = self._find(u)
    j = self._find(v)
    if i == j:
      return
    if self.rank[i] < self.rank[j]:
      self.id[i] = j
    elif self.rank[i] > self.rank[j]:
      self.id[j] = i
    else:
      self.id[i] = j
      self.rank[j] += 1
    self.count -= 1

  def getCount(self) -> int:
    return self.count

  def _find(self, u: int) -> int:
    if self.id[u] != u:
      self.id[u] = self._find(self.id[u])
    return self.id[u]


class Solution:
  def numberOfComponents(self, properties: list[list[int]], k: int) -> int:
    n = len(properties)
    uf = UnionFind(n)
    propertySets = [set(property) for property in properties]

    for i, j in itertools.combinations(range(n), 2):
      if len(propertySets[i] & propertySets[j]) >= k:
        uf.unionByRank(i, j)

    return uf.getCount()

class Solution:
  def minOperations(self, queries: list[list[int]]) -> int:
    return sum((self._getOperations(r) - self._getOperations(l - 1) + 1) // 2
               for l, r in queries)

  def _getOperations(self, n: int) -> int:
    """Returns the number of operations required for [1, n]."""
    res = 0
    ops = 0
    powerOfFour = 1
    while powerOfFour <= n:
      l = powerOfFour
      r = min(n, powerOfFour * 4 - 1)
      ops += 1
      res += (r - l + 1) * ops
      powerOfFour *= 4
    return res

class Solution:
  def maxScore(self, nums: list[int]) -> int:
    summ = sum(nums)
    if len(nums) % 2 == 1:
      return summ - min(nums)
    return summ - min(a + b for a, b in itertools.pairwise(nums))

class Solution:
  def reverseDegree(self, s: str) -> int:
    return sum((26 - (ord(c) - ord('a'))) * (i + 1)
               for i, c in enumerate(s))

class Solution:
  def maxActiveSectionsAfterTrade(self, s: str) -> int:
    zeroGroups = [len(list(g)) for c, g in itertools.groupby(s) if c == '0']
    return s.count('1') + max(map(sum, pairwise(zeroGroups)), default=0)

class Solution:
  def minimumCost(self, nums: list[int], cost: list[int], k: int) -> int:
    n = len(nums)
    prefixNums = list(itertools.accumulate(nums, initial=0))
    prefixCost = list(itertools.accumulate(cost, initial=0))
    # dp[i] := the minimum cost to divide nums[i..n - 1] into subarrays
    dp = [math.inf] * n + [0]

    for i in range(n - 1, -1, -1):
      for j in range(i, n):
        dp[i] = min(dp[i],
                    prefixNums[j + 1] * (prefixCost[j + 1] - prefixCost[i]) +
                    k * (prefixCost[n] - prefixCost[i]) + dp[j + 1])

    return dp[0]

class Solution:
  def minCosts(self, cost: list[int]) -> list[int]:
    return list(itertools.accumulate(cost, min))

class Solution:
  def minEliminationTime(self, timeReq: list[int], splitTime: int) -> int:
    minHeap = timeReq.copy()
    heapq.heapify(minHeap)
    heapq.heappop(minHeap)

    while True:
      bacterial = splitTime + heapq.heappop(minHeap)
      if not minHeap:
        return bacterial
      heapq.heappushpop(minHeap, bacterial)

from enum import Enum


class State(Enum):
  FIRST = 0  # first element - add to sum and start product
  SUBTRACT = 1  # second element - subtract from sum and multiply product
  ADD = 2  # third element - add to sum and multiply product


class Solution:
  def maxProduct(self, nums: list[int], k: int, limit: int) -> int:
    MIN = -5000
    if abs(k) > sum(nums):
      return -1

    @functools.lru_cache(None)
    def dp(i: int, product: int, state: State, k: int) -> int:
      if i == len(nums):
        return (product if k == 0 and state != State.FIRST and product <= limit
                else MIN)
      res = dp(i + 1, product, state, k)
      if state == State.FIRST:
        res = max(res, dp(i + 1, nums[i], State.SUBTRACT, k - nums[i]))
      if state == State.SUBTRACT:
        res = max(res, dp(i + 1, min(product * nums[i], limit + 1),
                          State.ADD, k + nums[i]))
      if state == State.ADD:
        res = max(res, dp(i + 1, min(product * nums[i], limit + 1),
                          State.SUBTRACT, k - nums[i]))
      return res

    ans = dp(0, 1, State.FIRST, k)
    return -1 if ans == MIN else ans

class Solution:
  def makeArrayPositive(self, nums: list[int]) -> int:
    MAX = 10**18
    ans = 0
    minSum = nums[0] + nums[1]

    for i in range(2, len(nums)):
      a, b, c = nums[i - 2], nums[i - 1], nums[i]
      minSum = min(minSum + c, a + b + c)
      if minSum <= 0:
        nums[i] = MAX
        minSum = MAX
        ans += 1

    return ans

class Solution:
  def minOperations(self, nums: list[int], k: int) -> int:
    return sum(nums) % k

class Solution:
  def uniqueXorTriplets(self, nums: list[int]) -> int:
    n = len(nums)
    if n < 3:
      return n
    return 1 << (int(math.log2(n)) + 1)

class Solution:
  def uniqueXorTriplets(self, nums: list[int]) -> int:
    n = len(nums)
    if n == 1:
      return 1
    pairs = set(nums[i] ^ nums[j]
                for i, j in itertools.combinations(range(n), 2))
    return len(set(pair ^ num
                   for pair in pairs
                   for num in nums))

class Solution:
  def findClosest(self, x: int, y: int, z: int) -> int:
    xz = abs(x - z)
    yz = abs(y - z)
    if xz == yz:
      return 0
    return 1 if xz < yz else 2

class Solution:
  def __init__(self):
    self.MAX = 10**6 + 1

  def smallestPalindrome(self, s: str, k: int) -> str:
    count = collections.Counter(s)
    if not self._isPalindromePossible(count):
      return ''

    halfCount, midLetter = self._getHalfCountAndMidLetter(count)
    totalPerm = self._calculateTotalPermutations(halfCount)
    if k > totalPerm:
      return ''
    leftHalf = self._generateLeftHalf(halfCount, k)
    return ''.join(leftHalf) + midLetter + ''.join(reversed(leftHalf))

  def _isPalindromePossible(self, count: collections.Counter) -> bool:
    oddCount = sum(1 for count in count.values() if count % 2 == 1)
    return oddCount <= 1

  def _getHalfCountAndMidLetter(self, count: collections.Counter) -> tuple[list[int], str]:
    halfCount = [0] * 26
    midLetter = ''
    for c, freq in count.items():
      halfCount[ord(c) - ord('a')] = freq // 2
      if freq % 2 == 1:
        midLetter = c
    return halfCount, midLetter

  def _calculateTotalPermutations(self, halfCount: list[int]) -> int:
    """Calculate the total number of possible permutations."""
    return self._countArrangements(halfCount)

  def _generateLeftHalf(self, halfCount: list[int], k: int) -> list[str]:
    """Generate the left half of the palindrome based on k."""
    halfLen = sum(halfCount)
    left = []
    for _ in range(halfLen):
      for i, freq in enumerate(halfCount):
        if freq == 0:
          continue
        halfCount[i] -= 1
        arrangements = self._countArrangements(halfCount)
        if arrangements >= k:
          left.append(chr(i + ord('a')))
          break
        else:
          k -= arrangements
          halfCount[i] += 1
    return left

  def _countArrangements(self, count: list[int]) -> int:
    """Calculate the number of possible arrangements of characters."""
    total = sum(count)
    res = 1
    for freq in count:
      res *= self._nCk(total, freq)
      if res >= self.MAX:
        return self.MAX
      total -= freq
    return res

  def _nCk(self, n: int, k: int) -> int:
    res = 1
    for i in range(1, min(k, n - k) + 1):
      res = res * (n - i + 1) // i
      if res >= self.MAX:
        return self.MAX
    return res

class Solution:
  def calculateScore(self, instructions: list[str], values: list[int]) -> int:
    n = len(instructions)
    ans = 0
    i = 0
    seen = set()

    while 0 <= i < n and i not in seen:
      seen.add(i)
      if instructions[i] == 'add':
        ans += values[i]
        i += 1
      elif instructions[i] == 'jump':
        i += values[i]

    return ans

class Solution:
  def maximumPossibleSize(self, nums: list[int]) -> int:
    ans = 0
    prev = 0

    for num in nums:
      if num >= prev:
        prev = num
        ans += 1

    return ans

class Solution:
  def findCommonResponse(self, responses: list[list[str]]) -> str:
    count = collections.Counter()

    for response in responses:
      for response in set(response):
        count[response] += 1

    maxFreq = max(count.values())
    return min([response
                for response, count in count.items()
                if count == maxFreq])

class Solution:
  def baseUnitConversions(self, conversions: list[list[int]]) -> list[int]:
    MOD = 1_000_000_007
    n = len(conversions) + 1
    ans = [0] * n
    ans[0] = 1
    q = collections.deque([0])
    graph = [[] for _ in range(n)]

    for u, v, factor in conversions:
      graph[u].append((v, factor))

    while q:
      u = q.popleft()
      for v, factor in graph[u]:
        ans[v] = (ans[u] * factor) % MOD
        q.append(v)

    return ans

class Solution:
  def maxProfit(self, n: int, edges: list[list[int]], score: list[int]) -> int:
    # need[i] := the bitmask representing all nodes that must be placed before
    # node i
    need = [0] * n
    # dp[mask] := the maximum profit achievable by placing the set of nodes
    # represented by `mask`
    dp = [-1] * (1 << n)
    dp[0] = 0

    for u, v in edges:
      need[v] |= 1 << u

    # Iterate over all subsets of nodes (represented by bitmask `mask`)
    for mask in range(1 << n):
      if dp[mask] == -1:
        continue
      # Determine the position of the next node to be placed (1-based).
      pos = mask.bit_count() + 1
      # Try to place each node `i` that hasn't been placed yet.
      for i in range(n):
        if mask >> i & 1:
          continue
        # Check if all dependencies of node `i` are already placed.
        if (mask & need[i]) == need[i]:
          newMask = mask | 1 << i  # Mark node `i` as placed.
          dp[newMask] = max(dp[newMask], dp[mask] + score[i] * pos)

    return dp[-1]

class UnionFind:
  def __init__(self, n: int):
    self.id = list(range(n))
    self.rank = [0] * n

  def unionByRank(self, u: int, v: int) -> None:
    i = self.find(u)
    j = self.find(v)
    if i == j:
      return
    if self.rank[i] < self.rank[j]:
      self.id[i] = j
    elif self.rank[i] > self.rank[j]:
      self.id[j] = i
    else:
      self.id[i] = j
      self.rank[j] += 1

  def find(self, u: int) -> int:
    if self.id[u] != u:
      self.id[u] = self.find(self.id[u])
    return self.id[u]


class Solution:
  def pathExistenceQueries(
      self,
      n: int,
      nums: list[int],
      maxDiff: int,
      queries: list[list[int]]
  ) -> list[bool]:
    uf = UnionFind(n)

    for i in range(1, n):
      if abs(nums[i] - nums[i - 1]) <= maxDiff:
        uf.unionByRank(i, i - 1)

    return [uf.find(u) == uf.find(v)
            for u, v in queries]

class Solution:
  def concatenatedDivisibility(self, nums: list[int], k: int) -> list[int]:
    n = len(nums)
    nums.sort()
    lengths = [len(str(num)) for num in nums]
    pows = [pow(10, length, k) for length in lengths]

    @functools.lru_cache(None)
    def dp(mask: int, mod: int) -> bool:
      """
      Returns True if there is a way to form a number divisible by `k` using the
      numbers in `nums`, where nums[i] is used iff `mask & (1 << i)`.
      """
      if mask == (1 << n) - 1:
        return mod == 0
      for i in range(n):
        if (mask >> i & 1) == 0:
          newMod = (mod * pows[i] + nums[i]) % k
          if dp(mask | 1 << i, newMod):
            return True
      return False

    def reconstruct(mask: int, mod: int) -> list[int]:
      """
      Reconstructs the numbers that form a number divisible by `k` using the
      numbers in `nums`, where nums[i] is used iff `mask & (1 << i)`.
      """
      for i in range(n):
        if (mask >> i & 1) == 0:
          newMod = (mod * pows[i] + nums[i]) % k
          if dp(mask | 1 << i, newMod):
            return [nums[i]] + reconstruct(mask | 1 << i, newMod)
      return []

    return reconstruct(0, 0) if dp(0, 0) else []

class Solution:
  def pathExistenceQueries(
      self,
      n: int,
      nums: list[int],
      maxDiff: int,
      queries: list[list[int]],
  ) -> list[int]:
    sortedNumAndIndexes = sorted((num, i) for i, num in enumerate(nums))
    sortedNums = [num for num, _ in sortedNumAndIndexes]
    indexMap = {originalIndex: sortedIndex for sortedIndex,
                (_, originalIndex) in enumerate(sortedNumAndIndexes)}
    maxLevel = n.bit_length() + 1
    # jump[i][j] is the index of the j-th ancestor of i
    jump = [[0] * maxLevel for _ in range(n)]

    right = 0
    for i in range(n):
      while right + 1 < n and sortedNums[right + 1] - sortedNums[i] <= maxDiff:
        right += 1
      jump[i][0] = right

    for level in range(1, maxLevel):
      for i in range(n):
        prevJump = jump[i][level - 1]
        jump[i][level] = jump[prevJump][level - 1]

    def minJumps(start: int, end: int, level: int) -> int:
      """
      Returns the minimum number of jumps from `start` to `end` using binary
      lifting.
      """
      if start == end:
        return 0
      if jump[start][0] >= end:
        return 1
      if jump[start][level] < end:
        return math.inf
      for j in range(level, -1, -1):
        if jump[start][j] < end:
          break
      return (1 << j) + minJumps(jump[start][j], end, j)

    def minDist(u: int, v: int) -> int:
      uIndex = indexMap[u]
      vIndex = indexMap[v]
      start = min(uIndex, vIndex)
      end = max(uIndex, vIndex)
      res = minJumps(start, end, maxLevel - 1)
      return res if res < math.inf else -1

    return [minDist(u, v) for u, v in queries]

class Solution:
  def queryConversions(
      self,
      conversions: list[list[int]],
      queries: list[list[int]]
  ) -> list[int]:
    self.MOD = 1_000_000_007
    units = self._baseUnitConversions(conversions)
    # By Fermat's little theorem.
    return [units[v] * self._modPow(units[u], self.MOD - 2) % self.MOD
            for u, v in queries]

  # Same as 3528. Unit Conversion I
  def _baseUnitConversions(self, conversions: list[list[int]]) -> list[int]:
    n = len(conversions) + 1
    res = [0] * n
    res[0] = 1
    q = collections.deque([0])
    graph = [[] for _ in range(n)]

    for u, v, factor in conversions:
      graph[u].append((v, factor))

    while q:
      u = q.popleft()
      for v, factor in graph[u]:
        res[v] = (res[u] * factor) % self.MOD
        q.append(v)

    return res

  def _modPow(self, x: int, n: int) -> int:
    if n == 0:
      return 1
    if n % 2 == 1:
      return x * self._modPow(x, n - 1) % self.MOD
    return self._modPow(x * x % self.MOD, n // 2)

class Solution:
  def maxProduct(self, n: int) -> int:
    s = sorted(str(n))
    return int(s[-1]) * int(s[-2])

class Solution:
  def minTravelTime(
      self,
      l: int,
      n: int,
      k: int,
      position: list[int],
      time: list[int]
  ) -> int:
    prefix = list(itertools.accumulate(time))

    @functools.lru_cache(None)
    def dp(i: int, skips: int, last: int) -> int:
      """
      Returns the minimum travel time to reach the last stop from i-th stop,
      with `skips` skips remaining, and the last stop being `last`.
      """
      if i == n - 1:
        return 0 if skips == 0 else math.inf
      res = math.inf
      rate = prefix[i] - (prefix[last - 1] if last > 0 else 0)
      end = min(n - 1, i + skips + 1)
      for j in range(i + 1, end + 1):
        distance = position[j] - position[i]
        res = min(res, distance * rate + dp(j, skips - (j - i - 1), i + 1))
      return res

    return dp(0, k, 0)

class Solution:
  def magicalSum(self, m: int, k: int, nums: list[int]) -> int:
    MOD = 1_000_000_007

    @functools.lru_cache(None)
    def dp(m: int, k: int, i: int, carry: int) -> int:
      """
      Returns the number of magical sequences of length `k` that can be formed
      from the first `i` numbers in `nums` with at most `m` elements.
      """
      if m < 0 or k < 0 or (m + carry.bit_count() < k):
        return 0
      if m == 0:
        return int(k == carry.bit_count())
      if i == len(nums):
        return 0
      res = 0
      for count in range(m + 1):
        contribution = math.comb(m, count) * pow(nums[i], count, MOD) % MOD
        newCarry = carry + count
        res += dp(m - count, k - (newCarry % 2),
                  i + 1, newCarry // 2) * contribution
        res %= MOD
      return res

    return dp(m, k, 0, 0)

class Solution:
  def minOperations(self, nums: list[int]) -> int:
    ans = 0
    stack = [0]

    for num in nums:
      while stack and stack[-1] > num:
        stack.pop()
      if not stack or stack[-1] < num:
        ans += 1
        stack.append(num)

    return ans

class Solution:
  def maxWeight(self, n: int, edges: list[list[int]], k: int, t: int) -> int:
    graph = [[] for _ in range(n)]
    # dp[u][i] := the set of possible path sums ending at node u with i edges
    dp = [defaultdict(set) for _ in range(n)]

    for u, v, w in edges:
      graph[u].append((v, w))

    for u in range(n):
      dp[u][0].add(0)  # zero edges = sum 0

    for i in range(k):
      for u in range(n):
        for currSum in dp[u][i]:
          for v, w in graph[u]:
            newSum = currSum + w
            if newSum < t:
              dp[v][i + 1].add(newSum)

    ans = -1

    for u in range(n):
      if k in dp[u]:
        ans = max(ans, max(dp[u][k]))

    return ans

class Solution:
  def subtreeInversionSum(
      self,
      edges: list[list[int]],
      nums: list[int],
      k: int
  ) -> int:
    n = len(edges) + 1
    parent = [-1] * n
    graph = [[] for _ in range(n)]

    for u, v in edges:
      graph[u].append(v)
      graph[v].append(u)

    @functools.lru_cache(None)
    def dp(u: int, stepsSinceInversion: int, inverted: bool) -> int:
      """
      Returns the maximum sum for subtree rooted at u, with
      `stepsSinceInversion` steps of inversion and `inverted` is true if the
      subtree is inverted.
      """
      num = -nums[u] if inverted else nums[u]
      negNum = -num
      for v in graph[u]:
        if v == parent[u]:
          continue
        parent[v] = u
        num += dp(v, min(k, stepsSinceInversion + 1), inverted)
        if stepsSinceInversion == k:
          negNum += dp(v, 1, not inverted)
      return max(num, negNum) if stepsSinceInversion == k else num

    return dp(0, k, False)

class Solution:
  def minDeletion(self, s: str, k: int) -> int:
    count = collections.Counter(s)
    if len(count) <= k:
      return 0
    freqs = sorted(count.values())
    return sum(freqs[:len(freqs) - k])

class Solution:
  def canPartitionGrid(self, grid: list[list[int]]) -> bool:
    totalSum = sum(map(sum, grid))

    def canPartition(grid: list[list[int]]) -> bool:
      runningSum = 0
      for row in grid:
        runningSum += sum(row)
        if runningSum * 2 == totalSum:
          return True
      return False

    return canPartition(grid) or canPartition(zip(*grid))

class Solution:
  def maxScore(self, n: int, edges: list[list[int]]) -> int:
    ans = 0
    graph = [[] for _ in range(n)]
    cycleSizes = []  # components where all nodes have degree 2
    pathSizes = []  # components that are not cycleSizes
    seen = set()

    for u, v in edges:
      graph[u].append(v)
      graph[v].append(u)

    for i in range(n):
      if i in seen:
        continue
      component = self._getComponent(graph, i, seen)
      if all(len(graph[u]) == 2 for u in component):
        cycleSizes.append(len(component))
      elif len(component) > 1:
        pathSizes.append(len(component))

    for cycleSize in cycleSizes:
      ans += self._calculateScore(n - cycleSize + 1, n, True)
      n -= cycleSize

    for pathSize in sorted(pathSizes, reverse=True):
      ans += self._calculateScore(n - pathSize + 1, n, False)
      n -= pathSize

    return ans

  def _getComponent(
      self,
      graph: list[list[int]],
      start: int,
      seen: set[int],
  ) -> list[int]:
    component = [start]
    seen.add(start)
    for u in component:
      for v in graph[u]:
        if v in seen:
          continue
        component.append(v)
        seen.add(v)
    return component

  def _calculateScore(self, left: int, right: int, isCycle: bool) -> int:
    window = collections.deque([right, right])
    score = 0
    for value in range(right - 1, left - 1, -1):
      windowValue = window.popleft()
      score += windowValue * value
      window.append(value)
    return score + window[0] * window[1] * isCycle

class Solution:
  def canPartitionGrid(self, grid: list[list[int]]) -> bool:
    summ = sum(map(sum, grid))

    def canPartition(grid: list[list[int]]) -> bool:
      topSum = 0
      seen = set()
      for i, row in enumerate(grid):
        topSum += sum(row)
        botSum = summ - topSum
        seen |= set(row)
        if topSum - botSum in (0, grid[0][0],  grid[0][-1], row[0]):
          return True
        if len(grid[0]) > 1 and i > 0 and topSum - botSum in seen:
          return True
      return False

    return (canPartition(grid) or
            canPartition(grid[::-1]) or
            canPartition(list(zip(*grid))[::-1]) or
            canPartition(list(zip(*grid))))

class Solution:
  def smallestIndex(self, nums: List[int]) -> int:
    return next((i
                 for i, num in enumerate(nums)
                 if self._getDigitSum(num) == i), -1)

  def _getDigitSum(self, num: int) -> int:
    return sum(int(digit) for digit in str(num))

class Solution:
  def minSwaps(self, nums: list[int]) -> int:
    ans = 0
    seen = set()
    sortedNums = sorted(nums, key=lambda x: (self._getDigitSum(x), x))
    numToIndex = {num: i for i, num in enumerate(sortedNums)}

    for i, num in enumerate(nums):
      if i in seen or numToIndex[num] == i:
        continue
      cycleSize = 0
      j = i
      while j not in seen:
        seen.add(j)
        j = numToIndex[nums[j]]
        cycleSize += 1
      ans += max(cycleSize - 1, 0)
    return ans

  def _getDigitSum(self, num: int) -> int:
    return sum(int(digit) for digit in str(num))

class Solution:
  # Similar to 3341. Find Minimum Time to Reach Last Room I
  def minMoves(self, matrix: list[str]) -> int:
    if matrix[-1][-1] == '#':
      return -1

    teleportPositions = [[] for _ in range(26)]

    for i, row in enumerate(matrix):
      for j, c in enumerate(row):
        if c not in ('.', '#'):
          teleportPositions[ord(c) - ord('A')].append((i, j))

    return self._dijkstra(matrix, teleportPositions,
                          (0, 0), (len(matrix) - 1, len(matrix[0]) - 1))

  def _dijkstra(
      self,
      matrix: list[str],
      teleportPositions: list[list[tuple[int, int]]],
      src: tuple[int, int],
      dst: tuple[int, int],
  ) -> int:
    DIRS = [(0, 1), (1, 0), (0, -1), (-1, 0)]
    m = len(matrix)
    n = len(matrix[0])
    dist = [[math.inf] * n for _ in range(m)]
    seen = set()

    dist[0][0] = 0
    minHeap = [(dist[0][0], src)]  # (d, u)

    while minHeap:
      d, u = heapq.heappop(minHeap)
      if u == dst:
        return d
      i, j = u
      if d > dist[i][j]:
        continue
      c = matrix[i][j]
      if c.isupper() and c not in seen:
        seen.add(c)
        for x, y in teleportPositions[ord(c) - ord('A')]:
          if d < dist[x][y]:
            dist[x][y] = d
            heapq.heappush(minHeap, (d, (x, y)))
      for dx, dy in DIRS:
        x = i + dx
        y = j + dy
        if x < 0 or x == m or y < 0 or y == n:
          continue
        if matrix[x][y] == '#':
          continue
        if d + 1 < dist[x][y]:
          dist[x][y] = d + 1
          heapq.heappush(minHeap, (d + 1, (x, y)))

    return -1

class Solution:
  def minSubarraySort(self, nums: list[int], k):
    ans = []

    for i in range(len(nums) - k + 1):
      window = nums[i:i+k]
      sortedWindow = sorted(window)
      l = 0
      r = k - 1
      while l < k and window[l] == sortedWindow[l]:
        l += 1
      while r >= 0 and window[r] == sortedWindow[r]:
        r -= 1
      ans.append(0 if l > r else r - l + 1)

    return ans

class Solution:
  def sumOfLargestPrimes(self, s: str) -> int:
    primes = set()
    n = len(s)

    for i in range(n):
      for j in range(i + 1, n + 1):
        num = int(s[i:j])
        if num not in primes and self._isPrime(num):
          primes.add(num)

    top3 = sorted(primes, reverse=True)[:3]
    return sum(top3)

  def _isPrime(self, n: int) -> bool:
    return n > 1 and all(n % i != 0 for i in range(2, math.isqrt(n) + 1))

class Solution:
  def maxSubstrings(self, word: str) -> int:
    ans = 0
    firstSeen = {}

    for i, c in enumerate(word):
      if c not in firstSeen:
        firstSeen[c] = i
      elif i - firstSeen[c] + 1 >= 4:
        ans += 1
        firstSeen.clear()

    return ans
