from enum import Enum


class State(Enum):
  INIT = 0
  VISITING = 1
  VISITED = 2


class Solution:
  def canFinish(self, numCourses: int, prerequisites: list[list[int]]) -> bool:
    graph = [[] for _ in range(numCourses)]
    states = [State.INIT] * numCourses

    for v, u in prerequisites:
      graph[u].append(v)

    def hasCycle(u: int) -> bool:
      if states[u] == State.VISITING:
        return True
      if states[u] == State.VISITED:
        return False
      states[u] = State.VISITING
      if any(hasCycle(v) for v in graph[u]):
        return True
      states[u] = State.VISITED
      return False

    return not any(hasCycle(i) for i in range(numCourses))

class Solution:
  def minSubArrayLen(self, target: int, nums: list[int]) -> int:
    ans = math.inf
    summ = 0
    j = 0

    for i, num in enumerate(nums):
      summ += num
      while summ >= target:
        ans = min(ans, i - j + 1)
        summ -= nums[j]
        j += 1

    return 0 if ans == math.inf else ans

