class Solution:
  def islandPerimeter(self, grid: list[list[int]]) -> int:
    m = len(grid)
    n = len(grid[0])

    islands = 0
    neighbors = 0

    for i in range(m):
      for j in range(n):
        if grid[i][j] == 1:
          islands += 1
          if i + 1 < m and grid[i + 1][j] == 1:
            neighbors += 1
          if j + 1 < n and grid[i][j + 1] == 1:
            neighbors += 1

    return islands * 4 - neighbors * 2

class Solution:
  def canIWin(self, maxChoosableInteger: int, desiredTotal: int) -> bool:
    if desiredTotal <= 0:
      return True

    totalSum = maxChoosableInteger * (maxChoosableInteger + 1) // 2
    if totalSum < desiredTotal:
      return False

    @functools.lru_cache(None)
    def dp(total: int, used: int) -> bool:
      """
      Returns true if the first player can we, where `used` represents the use
      numbers.
      """
      if total <= 0:
        return False
      return any((used >> i & 1) == 0
                 and not dp(total - i, used | 1 << i)
                 for i in range(1, maxChoosableInteger + 1))

    return dp(desiredTotal, 0)

class Solution:
  def minTransfers(self, transactions: list[list[int]]) -> int:
    balance = [0] * 21

    for u, v, amount in transactions:
      balance[u] -= amount
      balance[v] += amount

    debts = [b for b in balance if b]

    def dfs(s: int) -> int:
      while s < len(debts) and not debts[s]:
        s += 1
      if s == len(debts):
        return 0

      ans = math.inf

      for i in range(s + 1, len(debts)):
        if debts[i] * debts[s] < 0:
          debts[i] += debts[s]  # `debts[s]` is settled.
          ans = min(ans, 1 + dfs(s + 1))
          debts[i] -= debts[s]  # Backtrack.

      return ans

    return dfs(0)

class Solution:
  def findSubstringInWraproundString(self, s: str) -> int:
    maxLength = 1
    # count[i] := the number of substrings ending in ('a' + i)
    count = [0] * 26

    for i in range(len(s)):
      if i > 0 and (ord(s[i]) - ord(s[i - 1]) == 1
                    or ord(s[i - 1]) - ord(s[i]) == 25):
        maxLength += 1
      else:
        maxLength = 1
      index = ord(s[i]) - ord('a')
      count[index] = max(count[index], maxLength)

    return sum(count)

class Solution:
  def validIPAddress(self, queryIP: str) -> str:
    if queryIP.count('.') == 3:
      parts = queryIP.split('.')
      if len(parts) == 4 and all(self._isIPv4(part) for part in parts):
        return 'IPv4'

    if queryIP.count(':') == 7:
      parts = queryIP.split(':')
      if len(parts) == 8 and all(self._isIPv6(part) for part in parts):
        return 'IPv6'

    return 'Neither'

  def _isIPv4(self, digit: str) -> bool:
    if not digit or len(digit) > 3:
      return False
    if len(digit) > 1 and digit[0] == '0':
      return False
    if not all(c.isdigit() for c in digit):
      return False
    num = int(digit)
    return 0 <= num <= 255

  def _isIPv6(self, digit: str) -> bool:
    if not digit or len(digit) > 4:
      return False
    validChars = set('0123456789abcdefABCDEF')
    return all(c in validChars for c in digit)

class Solution:
  def isConvex(self, points: list[list[int]]) -> bool:
    def getCross(p: list[int], q: list[int], r: list[int]):
      """Returns pq x qr."""
      return (q[0] - p[0]) * (r[1] - p[1]) - (q[1] - p[1]) * (r[0] - p[0])

    sign = 0
    for i in range(len(points)):
      cross = getCross(points[i - 2], points[i - 1], points[i])
      if cross == 0:  # p, q, r are collinear.
        continue
      if sign == 0:  # Find the first cross that's not 0.
        sign = cross
      elif cross * sign < 0:
        return False

    return True

