class Solution:
  def encode(self, s: str) -> str:
    n = len(s)
    # dp[i][j] := the shortest encoded string of s[i..j]
    dp = [[''] * n for _ in range(n)]

    for d in range(n):
      for i in range(n - d):
        j = i + d
        curr = s[i:j + 1]
        dp[i][j] = curr

        if len(dp[i][j]) < 5:
          continue

        # Try all the possible partitions.
        for k in range(i, j):
          if len(dp[i][k]) + len(dp[k + 1][j]) < len(dp[i][j]):
            dp[i][j] = dp[i][k] + dp[k + 1][j]

        # Try to compress the string.
        # e.g. s = aabaabaab -> 3[aab]
        for k in range(i, j):
          pattern = s[i:k + 1]
          if len(curr) % len(pattern) == 0 and pattern * (len(curr) //
                                                          len(pattern)) == curr:
            candidate = f"{len(curr) // len(pattern)}[{dp[i][k]}]"
            if len(candidate) < len(dp[i][j]):
              dp[i][j] = candidate

    return dp[0][n - 1]

class Solution:
  def findAllConcatenatedWordsInADict(self, words: list[str]) -> list[str]:
    wordSet = set(words)

    @functools.lru_cache(None)
    def isConcat(word: str) -> bool:
      for i in range(1, len(word)):
        prefix = word[:i]
        suffix = word[i:]
        if prefix in wordSet and (suffix in wordSet or isConcat(suffix)):
          return True

      return False

    return [word for word in words if isConcat(word)]

class Solution:
  def makesquare(self, matchsticks: list[int]) -> bool:
    if len(matchsticks) < 4:
      return False

    perimeter = sum(matchsticks)
    if perimeter % 4 != 0:
      return False

    A = sorted(matchsticks)[::-1]

    def dfs(selected: int, edges: list[int]) -> bool:
      if selected == len(A):
        return all(edge == edges[0] for edge in edges)

      for i, edge in enumerate(edges):
        if A[selected] > edge:
          continue
        edges[i] -= A[selected]
        if dfs(selected + 1, edges):
          return True
        edges[i] += A[selected]

      return False

    return dfs(0, [perimeter // 4] * 4)

class Solution:
  def findMaxForm(self, strs: list[str], m: int, n: int) -> int:
    # dp[i][j] := the maximum size of the subset given i 0s and j 1s are
    # available
    dp = [[0] * (n + 1) for _ in range(m + 1)]

    for s in strs:
      zeros = s.count('0')
      ones = len(s) - zeros
      for i in range(m, zeros - 1, -1):
        for j in range(n, ones - 1, -1):
          dp[i][j] = max(dp[i][j], dp[i - zeros][j - ones] + 1)

    return dp[m][n]

