class Solution:
  def missingNumber(self, arr: list[int]) -> int:
    n = len(arr)
    delta = (arr[-1] - arr[0]) // n
    l = 0
    r = n - 1

    while l < r:
      m = (l + r) // 2
      if arr[m] == arr[0] + m * delta:
        l = m + 1
      else:
        r = m

    return arr[0] + l * delta

class Solution:
  def minAvailableDuration(
      self,
      slots1: list[list[int]],
      slots2: list[list[int]],
      duration: int,
  ) -> list[int]:
    slots1.sort()
    slots2.sort()

    i = 0  # slots1's index
    j = 0  # slots2's index

    while i < len(slots1) and j < len(slots2):
      start = max(slots1[i][0], slots2[j][0])
      end = min(slots1[i][1], slots2[j][1])
      if start + duration <= end:
        return [start, start + duration]
      if slots1[i][1] < slots2[j][1]:
        i += 1
      else:
        j += 1

    return []

class Solution:
  def probabilityOfHeads(self, prob: list[float], target: int) -> float:
    # dp[i][j] := the probability of tossing the first i coins with j heads
    dp = [[0] * (target + 1) for _ in range(len(prob) + 1)]
    dp[0][0] = 1.0

    for i in range(1, len(prob) + 1):
      for j in range(target + 1):
        dp[i][j] = ((dp[i - 1][j - 1] * prob[i - 1] if j > 0 else 0) +
                    dp[i - 1][j] * (1 - prob[i - 1]))

    return dp[len(prob)][target]

class Solution:
  def maximizeSweetness(self, sweetness: list[int], k: int) -> int:
    l = len(sweetness) // (k + 1)
    r = sum(sweetness) // (k + 1)

    def canEat(m: int) -> bool:
      """
      Returns True if can eat m sweetness (the minimum sweetness of each piece).
      """
      pieces = 0
      summ = 0  # the running sum
      for s in sweetness:
        summ += s
        if summ >= m:
          pieces += 1
          summ = 0
      return pieces > k

    while l < r:
      m = (l + r) // 2
      if canEat(m):
        l = m + 1
      else:
        r = m

    return l if canEat(l) else l - 1

