    mn = nums[0]

    for i in range(len(nums)):
      if nums[i] > mn:
        ans = max(ans, nums[i] - mn)
      mn = min(mn, nums[i])

    return ans

class Solution:
  def placeWordInCrossword(self, board: list[list[str]], word: str) -> bool:
