class Solution:
  def canChange(self, start: str, target: str) -> bool:
    n = len(start)
    i = 0  # start's index
    j = 0  # target's index

    while i <= n and j <= n:
      while i < n and start[i] == '_':
        i += 1
      while j < n and target[j] == '_':
        j += 1
      if i == n or j == n:
        return i == n and j == n
      if start[i] != target[j]:
        return False
      if start[i] == 'R' and i > j:
        return False
      if start[i] == 'L' and i < j:
        return False
      i += 1
      j += 1

    return True

class Solution:
  def minimumSwaps(self, nums: list[int]) -> int:
    minIndex = self._getLeftmostMinIndex(nums)
    maxIndex = self._getRightmostMaxIndex(nums)
    swaps = minIndex + (len(nums) - 1 - maxIndex)
    return swaps if minIndex <= maxIndex else swaps - 1

  def _getLeftmostMinIndex(self, nums: list[int]) -> int:
    mn = nums[0]
    minIndex = 0
    for i in range(1, len(nums)):
      if nums[i] < mn:
        mn = nums[i]
        minIndex = i
    return minIndex

  def _getRightmostMaxIndex(self, nums: list[int]) -> int:
    mx = nums[-1]
    maxIndex = len(nums) - 1
    for i in range(len(nums) - 2, -1, -1):
      if nums[i] > mx:
        mx = nums[i]
        maxIndex = i
    return maxIndex

class Solution:
  def numberOfPairs(self, nums: list[int]) -> list[int]:
    ans = [0] * 2
    count = collections.Counter(nums)

    for i in range(101):
      ans[0] += count[i] // 2
      ans[1] += count[i] & 1

    return ans

class Solution:
  def maximumSum(self, nums: list[int]) -> int:
    MAX = 9 * 9  # 999,999,999
    ans = -1
    count = [[] for _ in range(MAX + 1)]

    for num in nums:
      count[self._getDigitSum(num)].append(num)

    for groupNums in count:
      if len(groupNums) < 2:
        continue
      groupNums.sort(reverse=True)
      ans = max(ans, groupNums[0] + groupNums[1])

    return ans

  def _getDigitSum(self, num: int) -> int:
    return sum(int(digit) for digit in str(num))

class Solution:
  def minOperations(self, nums: list[int], numsDivide: list[int]) -> int:
    gcd = functools.reduce(math.gcd, numsDivide)

    for i, num in enumerate(sorted(nums)):
      if gcd % num == 0:
        return i

    return -1

class Solution:
  def visibleMountains(self, peaks: list[list[int]]) -> int:
    ans = 0
    maxRightFoot = 0

    peaks.sort(key=lambda x: (x[0] - x[1], -x[0]))

    for i, peak in enumerate(peaks):
      overlapWithNext = i + 1 < len(peaks) and peak == peaks[i + 1]
      currRightFoot = peak[0] + peak[1]
      if currRightFoot > maxRightFoot:
        if not overlapWithNext:
          ans += 1
        maxRightFoot = currRightFoot

    return ans

