class Solution:
  def maximumCostSubstring(self, s: str, chars: str, vals: list[int]) -> int:
    ans = 0
    cost = 0
    costs = [i for i in range(1, 27)]  # costs[i] := the cost of 'a' + i

    for c, val in zip(chars, vals):
      costs[ord(c) - ord('a')] = val

    for c in s:
      cost = max(0, cost + costs[ord(c) - ord('a')])
      ans = max(ans, cost)

    return ans

class Solution:
  def findTheLongestBalancedSubstring(self, s: str) -> int:
    ans = 0

    i = 0
    while i < len(s):
      zeros = 0
      ones = 0
      while i < len(s) and s[i] == '0':
        zeros += 1
        i += 1
      while i < len(s) and s[i] == '1':
        ones += 1
        i += 1
      ans = max(ans, min(zeros, ones))

    return ans * 2

class Solution:
  def findMatrix(self, nums: list[int]) -> list[list[int]]:
    # The number of rows we need equals the maximum frequency.
    ans = []
    count = [0] * (len(nums) + 1)

    for num in nums:
      count[num] += 1
      # Construct `ans` on demand.
      if count[num] > len(ans):
        ans.append([])
      ans[count[num] - 1].append(num)

    return ans

class Solution:
  def miceAndCheese(
      self,
      reward1: list[int],
      reward2: list[int],
      k: int,
  ) -> int:
