class Solution:
  def numberOfCategories(
      self,
      n: int,
      categoryHandler: Optional['CategoryHandler'],
  ) -> int:
    ans = 0

    for i in range(n):
      if not any(categoryHandler.haveSameCategory(i, j) for j in range(i)):
        ans += 1

    return ans

