class Solution:
  def validStrings(self, n: int) -> list[str]:
    ans = []

    def dfs(n: int, s: list[str]) -> None:
      if n == 0:
        ans.append(''.join(s))
        return
      if not s or s[-1] == '1':
        s.append('0')
        dfs(n - 1, s)
        s.pop()
      s.append('1')
      dfs(n - 1, s)
      s.pop()

    dfs(n, [])
    return ans

class Solution:
  def numberOfSubmatrices(self, grid: list[list[str]]) -> int:
    m = len(grid)
    n = len(grid[0])
    ans = 0
    # x[i][j] := the number of 'X' in grid[0..i)[0..j)
    x = [[0] * (n + 1) for _ in range(m + 1)]
    # y[i][j] := the number of 'Y' in grid[0..i)[0..j)
    y = [[0] * (n + 1) for _ in range(m + 1)]

    for i, row in enumerate(grid):
      for j, cell in enumerate(row):
        x[i + 1][j + 1] = (cell == 'X') + x[i][j + 1] + x[i + 1][j] - x[i][j]
        y[i + 1][j + 1] = (cell == 'Y') + y[i][j + 1] + y[i + 1][j] - y[i][j]
        if x[i + 1][j + 1] > 0 and x[i + 1][j + 1] == y[i + 1][j + 1]:
          ans += 1

    return ans

class Solution:
  def minimumCost(self, target: str, words: list[str], costs: list[int]) -> int:
    n = len(target)
    # dp[i] := the minimum cost to construct target[0..i)
    dp = [0] + [math.inf] * n
    # minCost[c][word] := the minimum cost to construct word starting with `c`
    minCost: dict[str, dict[str, int]] = collections.defaultdict(dict)

    for word, cost in zip(words, costs):
      c = word[0]
      minCost[c][word] = min(minCost[c].get(word, math.inf), cost)

    for i, c in enumerate(target):
      for word, cost in minCost[c].items():
        j = i + len(word)
        if j <= n and cost + dp[i] < dp[j] and target[i:j] == word:
          dp[j] = cost + dp[i]

    return -1 if dp[n] == math.inf else dp[n]

class Solution:
  def getSmallestString(self, s: str) -> str:
