    return max(
        (val1 + val2 + val3 for
         (val1, i1, j1),
         (val2, i2, j2),
         (val3, i3, j3) in (itertools.combinations(topNine, 3))
         if len({i1, i2, i3}) == 3 and len({j1, j2, j3}) == 3))

class Solution:
  def countKConstraintSubstrings(self, s: str, k: int) -> int:
    ans = 0
    count = [0, 0]

    l = 0
    for r, c in enumerate(s):
      count[int(c)] += 1
      while min(count) > k:
        count[int(s[l])] -= 1
        l += 1
      ans += r - l + 1

    return ans

