            collections.Counter(t[i:i + n // k] for i in range(0, n, n // k)))

class Solution:
  def minArraySum(self, nums: list[int], k: int, op1: int, op2: int) -> int:
    n = len(nums)
    # dp[i][j][k] := the minimum sum of nums[i..n - 1] with j operations of op1
    # and k operations of op2
    dp = [[[math.inf] * (op2 + 1)
          for _ in range(op1 + 1)]
          for _ in range(n + 1)]

    # Base case: When index reaches the end of the array, the result is 0.
    for i in range(op1 + 1):
      for j in range(op2 + 1):
        dp[n][i][j] = 0

    for i in range(n - 1, -1, -1):
      for o1 in range(op1 + 1):
        for o2 in range(op2 + 1):
          summ = nums[i] + dp[i + 1][o1][o2]
          if o1 > 0:
            summ = min(summ, (nums[i] + 1) // 2 + dp[i + 1][o1 - 1][o2])
          if o2 > 0 and nums[i] >= k:
            summ = min(summ, nums[i] - k + dp[i + 1][o1][o2 - 1])
          if o1 > 0 and o2 > 0:
            if (nums[i] + 1) // 2 >= k:
              summ = min(summ,
                         (nums[i] + 1) // 2 - k + dp[i + 1][o1 - 1][o2 - 1])
            if nums[i] >= k:
              summ = min(summ,
                         (nums[i] - k + 1) // 2 + dp[i + 1][o1 - 1][o2 - 1])
          dp[i][o1][o2] = summ

    return dp[0][op1][op2]

class Solution:
  def smallestNumber(self, n: int) -> int:
    return (1 << n.bit_length()) - 1

class Solution:
  def getLargestOutlier(self, nums: list[int]) -> int:
    ans = -math.inf
    summ = sum(nums)
    count = collections.Counter(nums)

    for num in nums:
      withoutNum = summ - num
      if withoutNum % 2 == 0:
        specialSum = withoutNum // 2  # the sum of special numbers
        if count[specialSum] > (1 if num == specialSum else 0):
          ans = max(ans, num)

    return ans

class Solution:
  def maxTargetNodes(
      self,
      edges1: list[list[int]],
      edges2: list[list[int]],
      k: int
  ) -> list[int]:
    graph1 = self._buildGraph(edges1)
    graph2 = self._buildGraph(edges2)
    maxReachableInGraph2 = 0

    if k > 0:
      for i in range(len(edges2) + 1):
        maxReachableInGraph2 = max(maxReachableInGraph2,
                                   self._dfs(graph2, i, -1, k - 1))

    return [maxReachableInGraph2 + self._dfs(graph1, i, -1, k)
            for i in range(len(edges1) + 1)]

  def _dfs(self, graph: list[list[int]], u: int, prev: int, k: int) -> int:
    """Returns the number of nodes that can be reached from u with k steps."""
    if k == 0:
      return 1
    res = 0
    for v in graph[u]:
      if v != prev:
        res += self._dfs(graph, v, u, k - 1)
    return 1 + res

  def _buildGraph(self, edges: list[list[int]]) -> list[list[int]]:
    graph = [[] for _ in range(len(edges) + 1)]
    for u, v in edges:
      graph[u].append(v)
      graph[v].append(u)
    return graph

class Solution:
  def minOperations(self, nums: list[int], k: int) -> int:
    numsSet = set(nums)
    mn = min(nums)
    if mn < k:
      return -1
    if mn > k:
      return len(numsSet)
    return len(numsSet) - 1

class Solution:
  def minOperations(self, n: int, m: int) -> int:
    isPrime = self._sieveEratosthenes(10000)
    if isPrime[n] or isPrime[m]:
      return -1
    return self._dijkstra(n, m, isPrime)

  def _dijkstra(self, src: int, dst: int, isPrime: list[bool]) -> int:
    seen = {src}
    minHeap = [(src, src)]  # (cost, num)

    while minHeap:
      cost, curr = heapq.heappop(minHeap)
      if curr == dst:
        return cost
      s = list(str(curr))
      for i, c in enumerate(s):
        if c < '9':
          s[i] = str(int(c) + 1)
          nextNum = int(''.join(s))
          if not isPrime[nextNum] and nextNum not in seen:
            heapq.heappush(minHeap, (cost + nextNum, nextNum))
            seen.add(nextNum)
          s[i] = c
        if c > '0' and not (i == 0 and c == '1'):
          s[i] = str(int(c) - 1)
          nextNum = int(''.join(s))
          if not isPrime[nextNum] and nextNum not in seen:
            heapq.heappush(minHeap, (cost + nextNum, nextNum))
            seen.add(nextNum)
          s[i] = c

    return -1

  def _sieveEratosthenes(self, n: int) -> list[bool]:
    isPrime = [True] * n
    isPrime[0] = False
    isPrime[1] = False
    for i in range(2, int(n**0.5) + 1):
      if isPrime[i]:
        for j in range(i * i, n, i):
          isPrime[j] = False
    return isPrime

class UnionFind:
  def __init__(self):
    self.id = {}
    self.rank = collections.Counter()

  def unionByRank(self, u: int, v: int) -> None:
    i = self.find(u)
    j = self.find(v)
    if i == j:
      return
    if self.rank[i] < self.rank[j]:
      self.id[i] = j
    elif self.rank[i] > self.rank[j]:
      self.id[j] = i
    else:
      self.id[i] = j
      self.rank[j] += 1

  def find(self, u: int) -> int:
    if u not in self.id:
      self.id[u] = u
    if self.id[u] != u:
      self.id[u] = self.find(self.id[u])
    return self.id[u]


class Solution:
  def countComponents(self, nums: list[int], threshold: int) -> int:
    uf = UnionFind()

    for num in nums:
      for multiple in range(2 * num, threshold + 1, num):
        uf.unionByRank(num, multiple)

    return len(set(uf.find(num) for num in nums))

class Solution:
  def constructTransformedArray(self, nums: list[int]) -> list[int]:
