      return res + runningLen // (k + 1)

    return bisect_left(range(1, len(s) + 1),
                       True, key=lambda m: getMinOps(m) <= numOps)

class Solution:
  def maximumMatchingIndices(self, nums1: list[int], nums2: list[int]) -> int:
    n = len(nums1)
    ans = 0

    for shift in range(n):
      matches = 0
      for i, num2 in enumerate(nums2):
        if nums1[(i + shift) % n] == num2:
          matches += 1
      ans = max(ans, matches)

    return ans

class Solution:
  def minimumOperations(self, grid: list[list[int]]) -> int:
    ans = 0

    for j in range(len(grid[0])):
      for i in range(1, len(grid)):
        if grid[i][j] <= grid[i - 1][j]:
          ans += grid[i - 1][j] - grid[i][j] + 1
          grid[i][j] = grid[i - 1][j] + 1

    return ans

class Solution:
  def countGoodArrays(self, n: int, m: int, k: int) -> int:
    MOD = 1_000_000_007
    return m * pow(m - 1, n - k - 1, MOD) * math.comb(n - 1, k) % MOD

class Solution:
  def hasMatch(self, s: str, p: str) -> bool:
    prefix, suffix = p.split('*')
    i = s.find(prefix)
    return i != -1 and s.find(suffix, i + len(prefix)) != -1

class Solution:
  def longestSubsequence(self, nums: list[int]) -> int:
    mx = max(nums)
    # dp[num][diff] := the length of the longest subsequence ending in `num`
    # s.t. the last absolute difference between consecutive elements is `diff`
    dp = [[0] * (mx + 1) for _ in range(mx + 1)]

    for num in nums:
      for prev in range(1, mx + 1):
        diff = abs(num - prev)
        dp[num][diff] = max(dp[num][diff], dp[prev][diff] + 1)
      # dp[num][diff] := max(dp[num][j]) for j >= diff
      for j in range(mx - 1, -1, -1):
        dp[num][j] = max(dp[num][j], dp[num][j + 1])

    return max(map(max, dp))

class Solution:
  def maxLength(self, nums: list[int]) -> int:
    n = len(nums)
    ans = 0

    for i in range(n):
      prod = 1
      l = 1
      g = 0
      for j in range(i, n):
        prod *= nums[j]
        l = math.lcm(l, nums[j])
        g = math.gcd(g, nums[j])
        if prod == l * g:
          ans = max(ans, j - i + 1)

    return ans

class Solution:
  def calculateScore(self, s: str) -> int:
    ans = 0
    indices = [[] for _ in range(26)]

    for i, c in enumerate(s):
      index = ord(c) - ord('a')
      oppositeIndex = 25 - index
      if indices[oppositeIndex]:
        ans += i - indices[oppositeIndex].pop()
      else:
        indices[index].append(i)

    return ans

from dataclasses import dataclass


@dataclass(frozen=True)
class T:
  weight: int
  selected: tuple[int]

  def __iter__(self):
    yield self.weight
    yield self.selected


class Solution:
  def maximumWeight(self, intervals: list[list[int]]) -> list[int]:
    intervals = sorted((*interval, i) for i, interval in enumerate(intervals))

    @functools.lru_cache(None)
    def dp(i: int, quota: int) -> T:
      """
      Returns the maximum weight and the selected intervals for intervals[i..n),
      where `quota` is the number of intervals that can be selected.
      """
      if i == len(intervals) or quota == 0:
        return T(0, ())

      skip = dp(i + 1, quota)

      _, r, weight, originalIndex = intervals[i]
      j = bisect.bisect_right(intervals, (r, math.inf))
      nextRes = dp(j, quota - 1)
      pick = T(weight + nextRes.weight,
               sorted((originalIndex, *nextRes.selected)))
      return (pick if (pick.weight > skip.weight or
                       pick.weight == skip.weight and pick.selected < skip.selected)
              else skip)

    return list(dp(0, 4).selected)

class Solution:
  def zigzagTraversal(self, grid: list[list[int]]) -> list[int]:
    zigzag = [row[::-1] if i % 2 else row for i, row in enumerate(grid)]
    return [num for row in zigzag for num in row][::2]

class Solution:
  def maximumAmount(self, coins: list[list[int]]) -> int:
    m = len(coins)
    n = len(coins[0])
    # dp[i][j][k] := the maximum profit at position (i, j) with k remaining
    # neutralizations
    dp = [[[-math.inf] * 4 for _ in range(n)] for _ in range(m)]

    # Base case: the robot starts at the top-left corner.
    dp[0][0][2] = coins[0][0]
    if coins[0][0] < 0:
      dp[0][0][1] = 0  # Neutralize the robber.

    for i in range(m):
      for j in range(n):
        for k in range(3):  # for each number of remaining neutralizations
          if i > 0:
            dp[i][j][k] = max(dp[i][j][k],
                              dp[i - 1][j][k] + coins[i][j],
                              dp[i - 1][j][k + 1])
          if j > 0:
            dp[i][j][k] = max(dp[i][j][k],
                              dp[i][j - 1][k] + coins[i][j],
                              dp[i][j - 1][k + 1])

    return max(dp[-1][-1])

class Solution:
  def minMaxWeight(self, n: int, edges: list[list[int]], threshold: int) -> int:
    MAX = 1000000
    reversedGraph = [[] for _ in range(n)]

    for u, v, w in edges:
      reversedGraph[v].append((u, w))

    l = 1
    r = MAX + 1

    while l < r:
      m = (l + r) // 2
      if self._dfs(reversedGraph, 0, m, set()) == n:
        r = m
      else:
        l = m + 1

    return -1 if l == MAX + 1 else l

  def _dfs(
      self,
      reversedGraph: list[list[tuple]],
      u: int,
      maxWeight: int,
      seen: set[int]
  ) -> int:
    """Returns the number of nodes reachable from u with weight <= maxWeight."""
    res = 1
    seen.add(u)
    for v, w in reversedGraph[u]:
      if w > maxWeight or v in seen:
        continue
      res += self._dfs(reversedGraph, v, maxWeight, seen)
    return res

class Solution:
  def maxAdjacentDistance(self, nums: list[int]) -> int:
