               for i in range(len(nums)))

class Solution:
  def minCost(self, arr: list[int], brr: list[int], k: int) -> int:
    def cost(arr: list[int], brr: list[int]) -> int:
      return sum(abs(a - b) for a, b in zip(arr, brr))
    return min(cost(arr, brr), cost(sorted(arr), sorted(brr)) + k)

class Solution:
  def subarraySum(self, nums: list[int]) -> int:
    prefix = list(itertools.accumulate(nums, initial=0))
    return sum(prefix[i + 1] - prefix[max(0, i - num)]
               for i, num in enumerate((nums)))

class Solution:
  def minCost(self, n: int, costs: list[list[int]]) -> int:
    INVALID_COLOR = 3

    def getValidColors(prevColor: int) -> list[int]:
      return [color for color in range(3) if color != prevColor]

    @functools.lru_cache(None)
    def minCost(i: int, prevLeftColor: int, prevRightColor: int) -> int:
      if i == len(costs) // 2:
        return 0
      res = math.inf
      for leftColor in getValidColors(prevLeftColor):
        for rightColor in getValidColors(prevRightColor):
          if leftColor == rightColor:
            continue
          leftCost = costs[i][leftColor]
          rightCost = costs[-1 - i][rightColor]
          res = min(res, leftCost + rightCost +
                    minCost(i + 1, leftColor, rightColor))
      return res

    return minCost(0, INVALID_COLOR, INVALID_COLOR)

class Solution:
  def minUnlockedIndices(self, nums: list[int], locked: list[int]) -> int:
    first2 = next((i for i, x in enumerate(nums) if x == 2), -1)
    first3 = next((i for i, x in enumerate(nums) if x == 3), -1)
    last1 = next((i for i, x in reversed(list(enumerate(nums))) if x == 1), -1)
    last2 = next((i for i, x in reversed(list(enumerate(nums))) if x == 2), -1)
    if first3 != -1 and last1 != -1 and first3 < last1:
      return -1
    return (sum(locked[i] == 1 for i in range(first2, last1)
                if first2 != -1 and last1 != -1) +
            sum(locked[i] == 1 for i in range(first3, last2)
                if first3 != -1 and last2 != -1))

from dataclasses import dataclass


@dataclass(frozen=True)
class OfflineUser:
  returnTimestamp: int
  userId: int

  def __lt__(self, other):
    return self.returnTimestamp < other.returnTimestamp


class Solution:
  def countMentions(
      self,
      numberOfUsers: int,
      events: list[list[str]]
  ) -> list[int]:
    ans = [0] * numberOfUsers
    online = [True] * numberOfUsers
    offlineQueue = []  # min-heap to track users that are offline
    allMentionsCount = 0

    events.sort(key=lambda x: (int(x[1]), -ord(x[0][0])))

    for eventType, t, messageContent in events:
      timestamp = int(t)
      # Bring users back online if their offline period has ended.
      while offlineQueue and offlineQueue[0].returnTimestamp <= timestamp:
        user = heapq.heappop(offlineQueue)
        online[user.userId] = True
      if eventType == "MESSAGE":
        match messageContent:
          case "ALL":
            allMentionsCount += 1
          case "HERE":
            for userId in range(numberOfUsers):
              if online[userId]:
                ans[userId] += 1
          case _:
            for userId in [int(part[2:]) for part in messageContent.split()]:
              ans[userId] += 1
      elif eventType == "OFFLINE":
        userId = int(messageContent)
        online[userId] = False
        # Add to queue to bring back online after 60 units.
        heapq.heappush(offlineQueue, OfflineUser(timestamp + 60, userId))

    # Add the "ALL" mentions to all users.
    for userId in range(numberOfUsers):
      ans[userId] += allMentionsCount
    return ans

class Solution:
  def maxFrequency(self, nums: list[int], k: int) -> int:
    return nums.count(k) + max(self._kadane(nums, target, k)
                               for target in range(1, 51)
                               if target != k)

  def _kadane(self, nums: list[int], target: int, k: int) -> int:
    """
    Returns the maximum achievable frequency of `k` by Kakane's algorithm,
    where each `target` in subarrays is transformed to `k`.
    """
    maxSum = 0
    sum = 0
    for num in nums:
      if num == target:
        sum += 1
      elif num == k:
        sum -= 1
      if sum < 0:  # Reset sum if it becomes negative (Kadane's spirit).
        sum = 0
      maxSum = max(maxSum, sum)
    return maxSum

from enum import Enum


class State(Enum):
  INIT = 0
  VISITING = 1
  VISITED = 2


class Solution:
  def supersequences(self, words: list[str]) -> list[list[int]]:
    ans = []
    edges = [(string.ascii_lowercase.index(words[0]),
              string.ascii_lowercase.index(words[1]))
             for words in words]
    nodes = sorted({u for u, _ in edges} | {v for _, v in edges})
    letterToIndex = {letter: i for i, letter in enumerate(nodes)}
    graph = [[] for _ in range(len(nodes))]

    for u, v in edges:
      graph[letterToIndex[u]].append(letterToIndex[v])

    for doubledSubset in self._getMinimumSubsets(graph):
      freq = [0] * 26
      for letter in nodes:
        freq[letter] = 1
      for index in doubledSubset:
        freq[nodes[index]] = 2
      ans.append(freq)

    return ans

  def _getMinimumSubsets(self, graph: list[list[int]]) -> list[tuple[int]]:
    """
    Returns a list of the minimum subsets of nodes that do not create a cycle
    when skipped.
    """
    n = len(graph)
    for subsetSize in range(n + 1):
      doubleSubsets = []
      for doubledSubset in itertools.combinations(range(n), subsetSize):
        if not self._hasCycleSkipping(graph, set(doubledSubset)):
          doubleSubsets.append(doubledSubset)
      if doubleSubsets:
        return doubleSubsets
    return []

  def _hasCycleSkipping(
      self,
      graph: list[list[int]],
      doubledSubset: set[int]
  ) -> bool:
    """
    Returns True if there is a cycle in the `graph` when skipping any edges
    whose both endpoints are in `doubledSubset`.
    """
    states = [State.INIT] * len(graph)

    def hasCycle(u: int) -> bool:
      if states[u] == State.VISITING:
        return True
      if states[u] == State.VISITED:
        return False
      states[u] = State.VISITING
      if u not in doubledSubset:
        for v in graph[u]:
          if v in doubledSubset:
            continue
          if hasCycle(v):
            return True
      states[u] = State.VISITED
      return False

    return any(hasCycle(i) for i in range(len(graph)))

class Solution:
  def permute(self, n: int) -> list[list[int]]:
    ans = []
    used = [False] * (n + 1)

    def dfs(path: list[int]) -> None:
      if len(path) == n:
        ans.append(path.copy())
        return
      for num in range(1, n + 1):
        if used[num]:
          continue
        if path and path[-1] % 2 == num % 2:
          continue
        used[num] = True
        path.append(num)
        dfs(path)
        path.pop()
        used[num] = False

    dfs([])
    return ans

class Solution:
  def findValidPair(self, s: str) -> str:
    count = collections.Counter(s)
    return next((a + b
                for a, b in itertools.pairwise(s)
                if a != b and count[a] == int(a) and count[b] == int(b)), '')

class Solution:
  def maxDifference(self, s: str) -> int:
    count = collections.Counter(s)
    maxOdd = max((freq for freq in count.values()
                  if freq % 2 == 1), default=0)
    minEven = min((freq for freq in count.values()
                   if freq % 2 == 0), default=len(s))
    return maxOdd - minEven

class Solution:
  def maxDistance(self, s: str, k: int) -> int:
    return max(self._flip(s, k, 'NE'), self._flip(s, k, 'NW'),
               self._flip(s, k, 'SE'), self._flip(s, k, 'SW'))

  def _flip(self, s: str, k: int, direction: str) -> int:
    res = 0
    pos = 0
    opposite = 0

    for c in s:
      if c in direction:
        pos += 1
      else:
        pos -= 1
        opposite += 1
      res = max(res, pos + 2 * min(k, opposite))

    return res

class Solution:
  def sortMatrix(self, grid: list[list[int]]) -> list[list[int]]:
    n = len(grid)
    ans = [[0] * n for _ in range(n)]
    diag = collections.defaultdict(list)

    for i, row in enumerate(grid):
      for j, num in enumerate(row):
        diag[i - j].append(num)

    for key in diag:
      diag[key].sort(reverse=key < 0)

    for i in range(n):
      for j in range(n):
        ans[i][j] = diag[i - j].pop()

    return ans

class Solution:
  def assignElements(self, groups: list[int], elements: list[int]) -> list[int]:
    ans = []
    elementToMinIndex = {}

    for i, element in enumerate(elements):
      if element not in elementToMinIndex:
        elementToMinIndex[element] = i

    for num in groups:
      ans.append(self._getMinIndex(num, elementToMinIndex))

    return ans

  def _getMinIndex(self, num: int, elementToMinIndex: dict[int, int]) -> int:
    res = math.inf
    i = 1
    while i * i <= num:
      if num % i != 0:
        continue
      if i in elementToMinIndex:
        res = min(res, elementToMinIndex[i])
      if num // i != i and (num // i) in elementToMinIndex:
        res = min(res, elementToMinIndex[num // i])
      i += 1
    return -1 if res == math.inf else res

class Solution:
  def countSubstrings(self, s: str) -> int:
    ans = 0
    # dp[i][num][rem] := the number of first `i` digits of s that have a
    # remainder of `rem` when divided by `num`
    dp = [[[0] * 10 for _ in range(10)] for _ in range(len(s) + 1)]

    for i in range(1, len(s) + 1):
      digit = int(s[i - 1])
      for num in range(1, 10):
        for rem in range(num):
          dp[i][num][(rem * 10 + digit) % num] += dp[i - 1][num][rem]
        dp[i][num][digit % num] += 1
      ans += dp[i][digit][0]

    return ans

class Solution:
  def maxScore(self, points: list[int], m: int) -> int:
    def isPossible(minVal: int, m: int) -> bool:
      """
      Returns True if it is possible to achieve the maximum minimum value `x`
      with `m` number of moves.
      """
      moves = 0
      prevMoves = 0  # to track remaining moves from the previous point
      for i, point in enumerate(points):
        required = (minVal + point - 1) // point  # ceil(minVal / point)
        required = max(0, required - prevMoves)
        if required > 0:
          moves += 2 * required - 1
          prevMoves = required - 1
        elif i + 1 < len(points):
          moves += 1
          prevMoves = 0
        if moves > m:
          return False
      return True

    l = 0
    r = (m + 1) // 2 * points[0] + 1

    while l < r:
      mid = (l + r + 1) // 2
      if isPossible(mid, m):
        l = mid
      else:
        r = mid - 1

    return l

class Solution:
  def maxStudentsOnBench(self, students: list[list[int]]) -> int:
    benchToStudents = collections.defaultdict(set)
    for studentId, benchId in students:
      benchToStudents[benchId].add(studentId)
    return max(map(len, benchToStudents.values()), default=0)

class Solution:
  def sumOfGoodNumbers(self, nums: list[int], k: int) -> int:
    return sum(num for i, num in enumerate(nums)
               if (i - k < 0 or num > nums[i - k])
               and (i + k >= len(nums) or num > nums[i + k]))

class Solution:
  def hasSpecialSubstring(self, s: str, k: int) -> bool:
    return any(len(list(group)) == k for _, group in groupby(s))

class Solution:
  def lenOfVDiagonal(self, grid: list[list[int]]) -> int:
    DIRS = ((-1, 1), (1, 1), (1, -1), (-1, -1))

    @functools.lru_cache(None)
    def dfs(i: int, j: int, turned: bool, num: int, dir: int) -> int:
      if i < 0 or i == len(grid) or j < 0 or j == len(grid[0]):
        return 0
      if grid[i][j] != num:
        return 0

      nextNum = 0 if num == 2 else 2
      dx, dy = DIRS[dir]
      res = 1 + dfs(i + dx, j + dy, turned, nextNum, dir)

      if not turned:
        nextDir = (dir + 1) % 4
        nextDx, nextDy = DIRS[nextDir]
        res = max(res, 1 + dfs(i + nextDx, j + nextDy, 1, nextNum, nextDir))

      return res

    return max((1 + dfs(i + dx, j + dy, 0, 2, d)
                for i, row in enumerate(grid)
                for j, num in enumerate(row)
                if num == 1
                for d, (dx, dy) in enumerate(DIRS)),
               default=0)

class Solution:
  def longestCommonPrefix(self, s: str, t: str) -> int:
    i = 0  # s's index
    j = 0  # t's index
    canSkip = True

    while i < len(s) and j < len(t):
      if s[i] == t[j]:
        i += 1
        j += 1
      elif canSkip:
        i += 1
        canSkip = False
      else:
        return j

    return j

class Solution:
  def maxSum(self, grid: list[list[int]], limits: list[int], k: int) -> int:
    minHeap = []

    for row, limit in zip(grid, limits):
      row.sort(reverse=True)
      for i in range(limit):
        heapq.heappush(minHeap, row[i])
        if len(minHeap) == k + 1:
          heapq.heappop(minHeap)

    return sum(minHeap)

class Solution:
  def maxCoins(self, lane1: list[int], lane2: list[int]) -> int:
    n = len(lane1)
    # dp[i][k] := the maximum number of coins at i-th mile with k switches
    dp = [[-math.inf] * 3 for _ in range(n)]
    dp[0][0] = lane1[0]
    dp[0][1] = lane2[0]

    for i in range(1, n):
      dp[i][0] = max(0, dp[i - 1][0]) + lane1[i]
      dp[i][1] = max(0, dp[i - 1][0], dp[i - 1][1]) + lane2[i]
      dp[i][2] = max(dp[i - 1][1], dp[i - 1][2]) + lane1[i]

    return max(map(max, dp))

class Solution:
  def transformArray(self, nums: list[int]) -> list[int]:
    return ([0] * sum(num % 2 == 0 for num in nums) +
            [1] * sum(num % 2 == 1 for num in nums))

class Solution:
  def countArrays(self, original: list[int], bounds: list[list[int]]) -> int:
    mn, mx = bounds[0]

    for i in range(1, len(original)):
      diff = original[i] - original[i - 1]
      mn = max(mn + diff, bounds[i][0])
      mx = min(mx + diff, bounds[i][1])

    return max(0, mx - mn + 1)

class Solution:
  def minCost(self, nums: list[int]) -> int:
    n = len(nums)

    @functools.lru_cache(None)
    def dp(last: int, i: int) -> int:
      if i == n:  # Single element left.
        return nums[last]
      if i == n - 1:  # Two elements left.
        return max(nums[last], nums[i])
      a = max(nums[i], nums[i + 1]) + dp(last, i + 2)
      b = max(nums[last], nums[i]) + dp(i + 1, i + 2)
      c = max(nums[last], nums[i + 1]) + dp(i, i + 2)
      return min(a, b, c)

    return dp(0, 1)

class Solution:
  def permute(self, n: int, k: int) -> list[int]:
    ans = []
    isLookingForEven = True
    remainingNumbers = list(range(1, n + 1))

    for turn in range(n):
      remainingPermutations = (math.factorial((n - 1 - turn) // 2) *
                               math.factorial((n - turn) // 2))
      found = False
      for index, number in enumerate(remainingNumbers):
        if number % 2 != isLookingForEven and (turn > 0 or n % 2 == 1):
          continue
        if k <= remainingPermutations:
          ans.append(remainingNumbers.pop(index))
          isLookingForEven = ans[-1] % 2 == 0
          found = True
          break
        k -= remainingPermutations
      if not found:
        return []

    return ans

class Solution:
  def largestInteger(self, nums: list[int], k: int) -> int:
    if k == len(nums):
      return max(nums)
    count = collections.Counter(nums)
    if k == 1:
      return max([num for num in nums if count[num] == 1], default=-1)
    return max(
        nums[0] if count[nums[0]] == 1 else -1,
        nums[-1] if count[nums[-1]] == 1 else -1
    )

class Solution:
  # Similar to 516. Longest Palindromic Subsequence
  def longestPalindromicSubsequence(self, s: str, k: int) -> int:
    n = len(s)
    # dp[i][j][op] := the length of LPS(s[i..j]) with at most `op` operations
    dp = [[[0] * (k + 1) for _ in range(n)] for _ in range(n)]

    for i in range(n):
      for op in range(k + 1):
        dp[i][i][op] = 1

    for d in range(1, n):
      for i in range(n - d):
        j = i + d
        for op in range(k + 1):
          if s[i] == s[j]:
            dp[i][j][op] = 2 + dp[i + 1][j - 1][op]
          else:
            dp[i][j][op] = max(dp[i + 1][j][op], dp[i][j - 1][op])
            cost = self._getCost(s[i], s[j])
            if cost <= op:
              dp[i][j][op] = max(dp[i][j][op], 2 + dp[i + 1][j - 1][op - cost])

    return dp[0][n - 1][k]

  def _getCost(self, a: str, b: str) -> int:
    dist = abs(ord(a) - ord(b))
    return min(dist, 26 - dist)

class Solution:
  def generateString(self, str1: str, str2: str) -> str:
    n = len(str1)
    m = len(str2)
    sz = n + m - 1
    ans = [None] * sz
    modifiable = [True] * sz

    # 1. Handle all 'T' positions first.
    for i, tf in enumerate(str1):
      if tf == 'T':
        for j, c in enumerate(str2):
          pos = i + j
          if ans[pos] and ans[pos] != c:
            return ''
          ans[pos] = c
          modifiable[pos] = False

    # 2. Fill all remaining positions with 'a'.
    for i in range(sz):
      if not ans[i]:
        ans[i] = 'a'

    # 3. Handle all 'F' positions.
    for i in range(n):
      if str1[i] == 'F' and self._match(ans, i, str2):
        modifiablePos = self._lastModifiablePosition(i, m, modifiable)
        if modifiablePos == -1:
          return ''
        ans[modifiablePos] = 'b'
        modifiable[modifiablePos] = False

    return ''.join(ans)

  def _match(self, ans: list, i: int, s: str) -> bool:
    """Returns True if the substring of ans starting at `i` matches `s`."""
    for j, c in enumerate(s):
      if ans[i + j] != c:
        return False
    return True

  def _lastModifiablePosition(self, i: int, m: int, modifiable: list) -> int:
    """
    Finds the last modifiable position in the substring of ans starting at `i`.
    """
    modifiablePos = -1
    for j in range(m):
      pos = i + j
      if modifiable[pos]:
        modifiablePos = pos
    return modifiablePos

class Solution:
  def maxProfit(self, workers: list[int], tasks: list[list[int]]) -> int:
    totalProfit = 0
    skillToProfits = collections.defaultdict(list)

    for skill, profit in tasks:
      skillToProfits[skill].append(profit)

    for skill in skillToProfits:
      skillToProfits[skill].sort(reverse=True)

    for workerSkill in workers:
      if workerSkill in skillToProfits and skillToProfits[workerSkill]:
        profit = skillToProfits[workerSkill][0]
        skillToProfits[workerSkill].pop(0)
        totalProfit += profit

    return totalProfit + max(max(profits, default=0)
                             for profits in skillToProfits.values())

class Solution:
  def findMaxSum(self, nums1: list[int], nums2: list[int], k: int) -> list[int]:
    ans = [0] * len(nums1)
    numAndIndexes = sorted([(num, i) for i, num in enumerate(nums1)])
    minHeap = []

    firstIndex = numAndIndexes[0][1]
    heapq.heappush(minHeap, nums2[firstIndex])
    summ = nums2[firstIndex]

    for (prevNum, prevIndex), (currNum, currIndex) in itertools.pairwise(numAndIndexes):
      if currNum == prevNum:
        ans[currIndex] = ans[prevIndex]
      else:
        ans[currIndex] = summ
      heapq.heappush(minHeap, nums2[currIndex])
      summ += nums2[currIndex]
      if len(minHeap) == k + 1:
        summ -= heapq.heappop(minHeap)

    return ans

class Solution:
  def applySubstitutions(self, replacements: list[list[str]], text: str) -> str:
    replaceMap = {key: value for key, value in replacements}

    def evaluate(text: str) -> str:
      """
      Evaluates the text and replaces the placeholders with the values
      from the replace_map recursively.
      """
      res = []
      i = 0
      while i < len(text):
        if text[i] == '%':
          j = i + 1 + text[i + 1:].find('%')
          key = text[i + 1:j]
          value = replaceMap[key]
          res.append(evaluate(value))
          i = j + 1
        else:
          res.append(text[i])
          i += 1
      return ''.join(res)

    return evaluate(text)

class Solution:
  def totalNumbers(self, digits: list[int]) -> int:
    nums = set()

    for a, b, c in itertools.permutations(digits, 3):
      if a != 0 and c % 2 == 0:
        nums.add(a * 100 + b * 10 + c)

    return len(nums)

class TrieNode:
  def __init__(self):
    self.children: dict[str, TrieNode] = {}
    self.count = 0


class Trie:
  def __init__(self, k: int):
    self.k = k
    self.root = TrieNode()
    self.prefixLengthsCount = collections.Counter()
    self.prefixLengths = SortedList()

  def insert(self, word: str) -> None:
    node = self.root
    for i, c in enumerate(word):
      sz = i + 1
      node = node.children.setdefault(c, TrieNode())
      node.count += 1
      if node.count >= self.k:
        self.prefixLengthsCount[sz] += 1
        if self.prefixLengthsCount[sz] == 1:
          self.prefixLengths.add(-sz)

  def erase(self, word: str) -> None:
    node = self.root
    for i, c in enumerate(word):
      sz = i + 1
      node = node.children[c]
      if node.count == self.k:
        self.prefixLengthsCount[sz] -= 1
        if self.prefixLengthsCount[sz] == 0:
          self.prefixLengths.remove(-sz)
      node.count -= 1

  def getLongestCommonPrefix(self) -> int:
    return 0 if not self.prefixLengths else -self.prefixLengths[0]


class Solution:
  def longestCommonPrefix(self, words: list[str], k: int) -> list[int]:
    ans = []
    trie = Trie(k)

    for word in words:
      trie.insert(word)

    for word in words:
      trie.erase(word)
      ans.append(trie.getLongestCommonPrefix())
      trie.insert(word)

    return ans

class Solution:
  def maxSum(self, nums: list[int]) -> int:
    mx = max(nums)
